 * ConceptGraph Service
 * 
 * Manages the concept graph, providing methods to retrieve, update,
 * and subscribe to changes in the graph data.
 */

// In-memory concept graph storage
let conceptGraph = {
  alpha: 0,
  epsilon: 0,
  nodes: [],
  links: []
};

// Subscribers to graph updates
const subscribers = new Set();

const conceptGraphService = {
  /**
   * Get the complete concept graph
   * @param {string} graphId - Optional graph ID for multiple graphs (unused in this implementation)
   * @returns {Object} The concept graph data
   */
  getConceptGraph: (graphId) => {
    return { ...conceptGraph };
  },

  /**
   * Get a specific node by ID
   * @param {string} nodeId - The ID of the node to retrieve
   * @returns {Object} The node object or null if not found
   */
  getNode: (nodeId) => {
    const node = conceptGraph.nodes.find(n => n.id === nodeId);
    return node ? { ...node } : null;
  },

  /**
   * Update a node in the concept graph
   * @param {string} nodeId - The ID of the node to update
   * @param {Object} updates - Properties to update on the node
   * @returns {boolean} Success status
   */
  updateNode: (nodeId, updates) => {
    const nodeIndex = conceptGraph.nodes.findIndex(n => n.id === nodeId);
    if (nodeIndex === -1) return false;

    conceptGraph.nodes[nodeIndex] = {
      ...conceptGraph.nodes[nodeIndex],
      ...updates
    };

    // Notify subscribers
    notifySubscribers();
    return true;
  },

  /**
   * Add a new node to the graph
   * @param {Object} node - The node to add
   * @returns {Object} The added node
   */
  addNode: (node) => {
    if (!node.id) {
      node.id = `node_${Date.now()}`;
    }

    conceptGraph.nodes.push(node);
    notifySubscribers();
    return { ...node };
  },

  /**
   * Add a new link between nodes
   * @param {string} sourceId - Source node ID
   * @param {string} targetId - Target node ID
   * @param {Object} properties - Additional link properties
   * @returns {Object} The created link
   */
  addLink: (sourceId, targetId, properties = {}) => {
    const link = {
      source: sourceId,
      target: targetId,
      ...properties
    };

    conceptGraph.links.push(link);
    notifySubscribers();
    return { ...link };
  },

  /**
   * Subscribe to graph updates
   * @param {Function} callback - Function to call when graph updates
   * @returns {Function} Unsubscribe function
   */
  subscribeToUpdates: (callback) => {
    subscribers.add(callback);

    // Return unsubscribe function
    return () => {
      subscribers.delete(callback);
    };
  },

  /**
   * Set entire concept graph (used for initialization)
   * @param {Object} newGraph - The new graph data
   */
  setConceptGraph: (newGraph) => {
    conceptGraph = { ...newGraph };
    notifySubscribers();
  },

  /**
   * Clear the concept graph (reset to empty state)
   * Used for testing and reinitialization
   */
  clear: () => {
    conceptGraph = {
      alpha: 0,
      epsilon: 0,
      nodes: [],
      links: []
    };
    notifySubscribers();
  }
};

/**
 * Notify all subscribers of graph changes
 * @private
 */
function notifySubscribers() {
  const graphCopy = { ...conceptGraph };
  subscribers.forEach(callback => {
    try {
      callback(graphCopy);
    } catch (error) {
      console.error('Error in concept graph subscriber:', error);
    }
  });
}

export default conceptGraphService;","operationIsLocatedInWorkspace":true}

