    // Notify subscribers
    this.notifySubscribers('editorChange', {
      source: 'editor',
      content: this.editor.getValue(),
      event
    });
  }

  /**
   * Handle changes in cursor position within the editor
   * @param {Object} event - Cursor position change event
   */
  handleCursorPositionChange(event) {
