    if (conflictAnalysis.domains && conflictAnalysis.domains.length > 0) {
      const domain = conflictAnalysis.domains[0];
      
      if (domain.name === 'STATE_MANAGEMENT') {
        impact.scope = 'moderate';
        impact.affectedStatements = 3;
      } else if (domain.name === 'PERFORMANCE' && 
                (statement.toLowerCase().includes('global') || 
                 newInfo.toLowerCase().includes('global'))) {
        impact.scope = 'global';
        impact.affectedStatements = 5;
      }
    }
    
    return impact;
  }
  
  /**
   * Suggest intelligent resolutions for a conflict
   * @param {string} newInfo - The new information
   * @param {Object} conflict - The conflict details
   * @returns {Promise<Object>} - Resolution suggestions
   */
  async suggestResolution(newInfo, conflict) {
    const resolutions = [];
    const domains = conflict.domains || [];
    
    // Common for all conflicts
    resolutions.push({
      strategy: 'replace',
      description: 'Replace the existing information with the new information',
      updatedStatement: newInfo,
      confidence: 0.6
    });
    
    // Type-specific strategies
    if (conflict.type === this.conflictTypes.DIRECT.id) {
      // For direct contradictions, add contextual merge
      resolutions.push({
        strategy: 'contextual_merge',
        description: 'Merge with context conditions',
        updatedStatement: this.generateContextualMerge(conflict.statement, newInfo, conflict),
        confidence: 0.8
      });
    } else if (conflict.type === this.conflictTypes.SEMANTIC.id) {
      // For semantic conflicts, suggest hybrid approach
      resolutions.push({
        strategy: 'hybrid',
        description: 'Create a hybrid approach combining both statements',
        updatedStatement: this.generateHybridApproach(conflict.statement, newInfo, conflict),
        confidence: 0.85
      });
      
      // Add domain-specific resolutions
      if (domains.length > 0) {
        const domainName = domains[0].name;
        
        if (domainName === 'STATE_MANAGEMENT') {
          resolutions.push({
            strategy: 'layer',
            description: 'Layer state management approaches',
            updatedStatement: `For component-local state, ${newInfo.toLowerCase()}. For application-wide state, ${conflict.statement.toLowerCase()}`,
            confidence: 0.9
          });
        } else if (domainName === 'STYLING') {
          resolutions.push({
            strategy: 'theme',
            description: 'Use theme variables to accommodate both approaches',
            updatedStatement: `Use a theme system that enables both approaches: ${conflict.statement}. The theme should support: ${newInfo}`,
            confidence: 0.85
          });
        } else if (domainName === 'PERFORMANCE') {
          resolutions.push({
            strategy: 'profile',
            description: 'Profile to determine which approach is better',
            updatedStatement: `Profile to determine which approach works best in each case: ${conflict.statement} or ${newInfo}. Choose based on measured performance.`,
                    confidence: 0.8               // ? closes the PERF profile suggestion
      });
    }

    // ---- fallback manual-review resolution ----
    resolutions.push({
      strategy: 'manual_review',
      description: 'Flag this pair for human attention if no automatic strategy is clearly safe',
      updatedStatement: conflict.statement,
      confidence: 0.4
    });

    // de-dupe & sort by confidence
    return [...new Map(resolutions.map(r => [r.strategy, r])).values()]
           .sort((a, b) => b.confidence - a.confidence);
  }

  /* ------------------------------------------------------------------ *
   *  ? helper generators referenced above
   * ------------------------------------------------------------------ */

  // Build a context-guarded merge for direct contradictions
  generateContextualMerge(existingStmt, newStmt, conflict) {
    const { term1, term2 } = conflict.details ?? {};
    return `If ${term1 ?? 'the first condition'} applies ? ${existingStmt}; ` +
           `else (${term2 ?? 'alternative'}) ? ${newStmt}.`;
  }

  // Hybrid guideline (keeps both prescriptions)
  generateHybridApproach(existingStmt, newStmt) {
    return `${existingStmt.replace(/\.$/, '')} **and** ${newStmt}`;
  }

  // Utility: true if any element appears in both arrays
  hasCommonElement(a, b) {
    return a?.some(x => b?.includes(x));
  }
}

export default new SemanticConflictServiceEnhanced();

