  }, [playing, timeScale, selectedRange]);
  
  // Execute Python code with tracing
  const executeCode = () => {
    if (!pythonCode) {
      console.warn('No Python code provided for execution');
      return;
    }
    
    // Reset state
    setDivergences([]);
