  };
  
  // Step forward in time
  const stepForward = () => {
    setCurrentTime(time => Math.min(selectedRange[1], time + 1));
  };
  
  // Stop playback
  const stopPlayback = () => {
    setPlaying(false);
    setCurrentTime(selectedRange[0]);
  };
  
  // Change time scale
