                onClick={stepForward}
                disabled={playing || isTracing}
                aria-label="Step forward"
              >
                
              </button>
              
              <button 
                className="control-button"
                onClick={stopPlayback}
                disabled={isTracing}
                aria-label="Stop"
              >
                 Stop
              </button>
            </>
          )}
          
          <div className="meditation-time">
            t = {currentTime.toFixed(1)}
