    editorRef.current.setOption('theme', theme);
  }, [currentPersona, getFeature]);
  
  // Handle concept selection
  const handleConceptSelect = (nodeIds) => {
    setSelectedConceptIds(nodeIds);
    onConceptSelect(nodeIds);
  };
  
  // Toggle concept canvas visibility
  const toggleConceptCanvas = () => {
    setShowConceptCanvas(!showConceptCanvas);
  };
  
  // Cycle through layout options
  const cycleLayout = () => {
    const layouts = [
      'split-horizontal',
      'split-vertical',
      'split-vertical-concept-first',
      'editor-only',
      'concept-only'
    ];
    
    const currentIndex = layouts.indexOf(layout);
    const nextIndex = (currentIndex + 1) % layouts.length;
    setLayout(layouts[nextIndex]);
  };
  
  return (
    <div className={`concept-editor-panel ${layout}`} data-testid="concept-editor-panel">
      <div 
        className="code-editor-container"
        style={{ display: layout === 'concept-only' ? 'none' : 'flex' }}
      >
        <div
          ref={editorElementRef}
          className="code-editor"
          data-testid="code-editor"
          role="textbox"
          aria-label={`Code editor for ${filePath || 'untitled'}`}
        />
        
        <div className="editor-toolbar">
          <button
            onClick={toggleConceptCanvas}
            aria-label={showConceptCanvas ? 'Hide concept view' : 'Show concept view'}
            title={showConceptCanvas ? 'Hide concept view' : 'Show concept view'}
            className={showConceptCanvas ? 'active' : ''}
