/**
 * Integration Test for ExecutionTracer + FieldMeditationMode
 * 
 * Tests the interaction between the ExecutionTracerService and FieldMeditationMode
 * components, ensuring execution events are properly visualized.
 */

import React from 'react';
import { render, screen, waitFor, act } from '@testing-library/react';
import '@testing-library/jest-dom';
import mockExecutionTracerService from './mockExecutionTracerService';
import mockConceptGraphService from '../Exporter_ConceptGraphService/mockConceptGraphService';
import { MockFieldMeditationMode } from './mockComponents';

// Use the mocks for all tests
const executionTracerService = mockExecutionTracerService;
const conceptGraphService = mockConceptGraphService;

// Use mock component instead of the real one
jest.mock('../../../components/FieldMeditationMode/FieldMeditationMode', () => {
  return require('./mockComponents').MockFieldMeditationMode;
});

// Mock WebSocket
class MockWebSocket {
  constructor(url) {
    this.url = url;
    this.onopen = null;
    this.onmessage = null;
    this.onclose = null;
    this.onerror = null;
    this.readyState = 0; // CONNECTING
    setTimeout(() => {
      this.readyState = 1; // OPEN
      if (this.onopen) {
        this.onopen();
      }
    }, 50);
  }
  send(data) {
    const parsedData = JSON.parse(data);
    if (parsedData.type === 'execute_python') {
      setTimeout(() => {
        this.simulateExecutionStart();
        setTimeout(() => this.simulateExecutionEvent('node_1', 0.2), 100);
        setTimeout(() => this.simulateExecutionEvent('node_2', 0.5), 200);
        setTimeout(() => this.simulateExecutionEvent('node_3', 0.8), 300);
        setTimeout(() => this.simulateExecutionEnd(), 400);
      }, 50);
    }
  }
  simulateMessage(data) {
    if (this.onmessage) {
      this.onmessage({ data: JSON.stringify(data) });
    }
  }
  simulateExecutionStart() {
    this.simulateMessage({
      type: 'execution_start',
      payload: { timestamp: Date.now() }
    });
  }
  simulateExecutionEvent(nodeId, phase) {
    this.simulateMessage({
      type: 'execution_event',
      payload: { nodeId, phase, timestamp: Date.now() }
    });
  }
  simulateExecutionEnd() {
    this.simulateMessage({
      type: 'execution_end',
      payload: { timestamp: Date.now() }
    });
  }
  close() {
    this.readyState = 3;
    if (this.onclose) {
      this.onclose();
    }
  }
}

global.WebSocket = MockWebSocket;

describe('ExecutionTracer ↔ FieldMeditationMode', () => {
  let sampleConceptData;
  beforeEach(() => {
    sampleConceptData = { alpha: 0, epsilon: 0, nodes: [{ id: 'node_1' }, { id: 'node_2' }, { id: 'node_3' }], links: [] };
    jest.clearAllMocks();
  });

  test('Visualizes execution events and updates', async () => {
    render(
      <MockFieldMeditationMode
        conceptData={sampleConceptData}
        pythonCode="print('Hello, world!')"
        onDivergenceDetected={jest.fn()}
      />
    );
    await act(async () => {
      await executionTracerService.connect();
    });
    await waitFor(() => {
      expect(screen.getByText(/spectral entropy wave/i)).toBeInTheDocument();
    });
  });

  test('Divergence detection is triggered and displayed', async () => {
    conceptGraphService.getNode.mockImplementationOnce(nodeId => ({
      id: nodeId,
      label: `Node ${nodeId}`,
      type: 'function',
      phase: 0.8
    }));
    const onDivergenceDetected = jest.fn();
    render(
      <MockFieldMeditationMode
        conceptData={sampleConceptData}
        pythonCode="print('Hello, world!')"
        onDivergenceDetected={onDivergenceDetected}
      />
    );
    await act(async () => {
      await executionTracerService.connect();
    });
    act(() => {
      executionTracerService.executionState.currentNode = 'node_1';
      executionTracerService.executionState.currentPhase = 0.2;
      executionTracerService.detectDivergences();
    });
    await waitFor(() => {
      expect(onDivergenceDetected).toHaveBeenCalledWith(
        expect.objectContaining({
          type: 'phase_divergence',
          nodeId: 'node_1'
        })
      );
    });
    await act(async () => {
      executionTracerService.handleExecutionStart({});
      executionTracerService.executionState.divergences = [{
        type: 'phase_divergence',
        nodeId: 'node_1',
        nodeName: 'Node node_1',
        severity: 'warning'
      }];
      executionTracerService.emitTraceUpdate();
    });
    await waitFor(() => {
      expect(screen.getByText(/divergence warnings/i)).toBeInTheDocument();
    });
    expect(screen.getByText(/phase_divergence/i)).toBeInTheDocument();
  });
});

/**
 * Integration Test for Exporter + ConceptGraphService
 * 
 * Tests the round-trip functionality between concept graphs and generated code,
 * ensuring that lineage comments are properly maintained.
 */

// Import the services
import exporterService from '../../../services/exporterService';
import mockConceptGraphService from './mockConceptGraphService';

// Use the mock for all tests
const conceptGraphService = mockConceptGraphService;

describe('Exporter + ConceptGraphService Integration', () => {
  
  // Set up mock concept graph before each test
  beforeEach(() => {
    // Clear the concept graph service
    conceptGraphService.clear();
    
    // Mock the getConceptGraph method to return our test graph
    jest.spyOn(conceptGraphService, 'getConceptGraph').mockImplementation((graphId) => {
      if (graphId === 'test-graph') {
        return {
          id: 'test-graph',
          name: 'Test Graph',
          nodes: [
            {
              id: 'import_math',
              label: 'math',
              type: 'import',
              file: 'calculator.py'
            },
            {
              id: 'class_calculator',
              label: 'Calculator',
              type: 'class',
              file: 'calculator.py',
              lineNumber: 3
            },
            {
              id: 'method_add',
              label: 'add',
              type: 'method',
              classId: 'class_calculator',
              parameters: 'self, a, b',
              returnType: 'float',
              body: 'return a + b',
              file: 'calculator.py',
              lineNumber: 4
            },
            {
              id: 'method_subtract',
              label: 'subtract',
              type: 'method',
              classId: 'class_calculator',
              parameters: 'self, a, b',
              returnType: 'float',
              body: 'return a - b',
              file: 'calculator.py',
              lineNumber: 7
            },
            {
              id: 'function_square_root',
              label: 'square_root',
              type: 'function',
              parameters: 'x',
              returnType: 'float',
              body: 'return math.sqrt(x)',
              file: 'calculator.py',
              lineNumber: 10
            },
            {
              id: 'variable_pi',
              label: 'PI',
              type: 'variable',
              value: '3.14159',
              constant: true,
              file: 'calculator.py',
              lineNumber: 13
            }
          ],
          edges: [
            {
              source: 'class_calculator',
              target: 'method_add',
              type: 'contains'
            },
            {
              source: 'class_calculator',
              target: 'method_subtract',
              type: 'contains'
            },
            {
              source: 'function_square_root',
              target: 'import_math',
              type: 'uses'
            }
          ]
        };
      }
      return null;
    });
    
    // Mock the extractConceptsFromText method
    jest.spyOn(conceptGraphService, 'extractConceptsFromText').mockImplementation(async (text) => {
      // For this test, we'll consider only simple extraction to simulate lineage comment detection
      const entities = [];
      const relationships = [];
      
      // Extract ALAN-LINEAGE comments
      const lineageMatches = Array.from(text.matchAll(/ALAN-LINEAGE: ([a-z0-9_]+) \|/g));
      for (const match of lineageMatches) {
        const conceptId = match[1];
        entities.push({
          id: conceptId,
          name: conceptId.split('_').pop(), // Extract name from ID
          type: conceptId.split('_')[0]     // Extract type from ID
        });
      }
      
      return { entities, relationships };
    });
  });
  
  afterEach(() => {
    jest.restoreAllMocks();
  });
  
  test('generates Python code with lineage comments from concept graph', async () => {
    // Export graph to Python code
    const result = await exporterService.exportGraphToCode({
      graphId: 'test-graph',
      language: 'python',
      includeLineage: true,
      author: 'test-user'
    });
    
    // Verify the result contains the expected file
    expect(result.files).toHaveProperty('calculator.py');
    const code = result.files['calculator.py'];
    
    // Verify imports are included
    expect(code).toContain('import math');
    
    // Verify class is generated with correct structure
    expect(code).toContain('class Calculator:');
    
    // Verify methods are included in the class
    expect(code).toContain('def add(self, a, b): float');
    expect(code).toContain('return a + b');
    expect(code).toContain('def subtract(self, a, b): float');
    expect(code).toContain('return a - b');
    
    // Verify functions are generated
    expect(code).toContain('def square_root(x): float');
    expect(code).toContain('return math.sqrt(x)');
    
    // Verify variables are included
    expect(code).toContain('PI = 3.14159');
    
    // Verify lineage comments
    expect(code).toContain('# ALAN-LINEAGE: import_math');
    expect(code).toContain('# ALAN-LINEAGE: class_calculator');
    expect(code).toContain('# ALAN-LINEAGE: method_add');
    expect(code).toContain('# ALAN-LINEAGE: method_subtract');
    expect(code).toContain('# ALAN-LINEAGE: function_square_root');
    expect(code).toContain('# ALAN-LINEAGE: variable_pi');
    
    // All lineage comments should include author and timestamp
    expect(code).toContain('test-user');
  });
  
  test('generates JavaScript code with lineage comments from concept graph', async () => {
    // Export graph to JavaScript code
    const result = await exporterService.exportGraphToCode({
      graphId: 'test-graph',
      language: 'javascript',
      includeLineage: true,
      author: 'test-user'
    });
    
    // Verify the result contains the expected file
    expect(result.files).toHaveProperty('calculator.py');
    const code = result.files['calculator.py'];
    
    // Verify imports are included
    expect(code).toContain('import * from "math"');
    
    // Verify class is generated with correct structure
    expect(code).toContain('class Calculator {');
    
    // Verify methods are included in the class
    expect(code).toContain('add(self, a, b) {');
    expect(code).toContain('return a + b');
    expect(code).toContain('subtract(self, a, b) {');
    expect(code).toContain('return a - b');
    
    // Verify functions are generated
    expect(code).toContain('function square_root(x) {');
    expect(code).toContain('return math.sqrt(x)');
    
    // Verify variables are included
    expect(code).toContain('const PI = 3.14159;');
    
    // Verify lineage comments
    expect(code).toContain('// ALAN-LINEAGE: import_math');
    expect(code).toContain('// ALAN-LINEAGE: class_calculator');
    expect(code).toContain('// ALAN-LINEAGE: method_add');
    expect(code).toContain('// ALAN-LINEAGE: method_subtract');
    expect(code).toContain('// ALAN-LINEAGE: function_square_root');
    expect(code).toContain('// ALAN-LINEAGE: variable_pi');
  });
  
  test('can extract concept identifiers from lineage comments', async () => {
    // Create test code with lineage comments
    const testCode = `
// ALAN-LINEAGE: import_math | 2025-05-08T10:00:00Z | test-user
import * from "math";

// ALAN-LINEAGE: class_calculator | 2025-05-08T10:00:00Z | test-user
class Calculator {
  // ALAN-LINEAGE: method_add | 2025-05-08T10:00:00Z | test-user
  add(a, b) {
    return a + b;
  }
  
  // ALAN-LINEAGE: method_subtract | 2025-05-08T10:00:00Z | test-user
  subtract(a, b) {
    return a - b;
  }
}

// ALAN-LINEAGE: function_square_root | 2025-05-08T10:00:00Z | test-user
function square_root(x) {
  return math.sqrt(x);
}

// ALAN-LINEAGE: variable_pi | 2025-05-08T10:00:00Z | test-user
const PI = 3.14159;
`;

    // Extract concepts from the code using the mock implementation
    const { entities } = await conceptGraphService.extractConceptsFromText(testCode);
    
    // Verify that all concepts are extracted from lineage comments
    expect(entities).toHaveLength(6);
    
    // Verify specific entities
    const conceptIds = entities.map(e => e.id);
    expect(conceptIds).toContain('import_math');
    expect(conceptIds).toContain('class_calculator');
    expect(conceptIds).toContain('method_add');
    expect(conceptIds).toContain('method_subtract');
    expect(conceptIds).toContain('function_square_root');
    expect(conceptIds).toContain('variable_pi');
  });
  
  test('performs round-trip conversion from graph to code and back', async () => {
    // First, export graph to code
    const exportResult = await exporterService.exportGraphToCode({
      graphId: 'test-graph',
      language: 'python',
      includeLineage: true,
      author: 'test-user'
    });
    
    const pythonCode = exportResult.files['calculator.py'];
    
    // Mock the document ID for testing
    const documentId = 'calculator.py';
    
    // Mock addDocument method to capture the concepts extracted
    let extractedEntities = [];
    jest.spyOn(conceptGraphService, 'addDocument').mockImplementation(async (docId, content) => {
      const { entities } = await conceptGraphService.extractConceptsFromText(content);
      extractedEntities = entities;
      return { success: true };
    });
    
    // Now add the code back to the concept graph
    await conceptGraphService.addDocument(documentId, pythonCode, { language: 'python' });
    
    // Verify that all original concepts were extracted from the code
    expect(extractedEntities).toHaveLength(6);
    
    // Verify specific entities by ID
    const conceptIds = new Set(extractedEntities.map(e => e.id));
    expect(conceptIds.has('import_math')).toBe(true);
    expect(conceptIds.has('class_calculator')).toBe(true);
    expect(conceptIds.has('method_add')).toBe(true);
    expect(conceptIds.has('method_subtract')).toBe(true);
    expect(conceptIds.has('function_square_root')).toBe(true);
    expect(conceptIds.has('variable_pi')).toBe(true);
  });
  
  test('maintains lineage through multiple export-import cycles', async () => {
    // First export cycle
    const exportResult1 = await exporterService.exportGraphToCode({
      graphId: 'test-graph',
      language: 'python',
      includeLineage: true,
      author: 'user1'
    });
    
    const pythonCode1 = exportResult1.files['calculator.py'];
    
    // Mock addDocument and extractConceptsFromText to simulate adding back to graph
    let extractedEntities1 = [];
    conceptGraphService.extractConceptsFromText.mockImplementationOnce(async (text) => {
      // Original mock implementation
      const entities = [];
      const lineageMatches = Array.from(text.matchAll(/ALAN-LINEAGE: ([a-z0-9_]+) \|/g));
      for (const match of lineageMatches) {
        const conceptId = match[1];
        entities.push({
          id: conceptId,
          name: conceptId.split('_').pop(),
          type: conceptId.split('_')[0]
        });
      }
      extractedEntities1 = entities;
      return { entities, relationships: [] };
    });
    
    // Simulate adding the code back to the concept graph
    await conceptGraphService.addDocument('calculator.py', pythonCode1, { language: 'python' });
    
    // Now mock getConceptGraph to return a new graph based on the extracted entities
    conceptGraphService.getConceptGraph.mockImplementationOnce((graphId) => {
      if (graphId === 'test-graph-2') {
        return {
          id: 'test-graph-2',
          name: 'Test Graph 2',
          nodes: extractedEntities1.map(entity => ({
            id: entity.id,
            label: entity.name,
            type: entity.type,
            file: 'calculator.py'
          })),
          edges: []
        };
      }
      return null;
    });
    
    // Second export cycle with the new graph
    const exportResult2 = await exporterService.exportGraphToCode({
      graphId: 'test-graph-2',
      language: 'python',
      includeLineage: true,
      author: 'user2'
    });
    
    const pythonCode2 = exportResult2.files['calculator.py'];
    
    // Verify that lineage comments are maintained in the second generation
    expect(pythonCode2).toContain('# ALAN-LINEAGE: import_math');
    expect(pythonCode2).toContain('# ALAN-LINEAGE: class_calculator');
    expect(pythonCode2).toContain('# ALAN-LINEAGE: method_add');
    expect(pythonCode2).toContain('# ALAN-LINEAGE: method_subtract');
    expect(pythonCode2).toContain('# ALAN-LINEAGE: function_square_root');
    expect(pythonCode2).toContain('# ALAN-LINEAGE: variable_pi');
    
    // Verify that the new author is added
    expect(pythonCode2).toContain('user2');
  });
  
  test('exports graph to snapshot format', async () => {
    // Mock field state
    const fieldState = {
      layout: 'force',
      centerX: 500,
      centerY: 300,
      zoom: 1.2,
      highlightedNodes: ['function_square_root'],
      expandedNodes: ['class_calculator'],
      viewportWidth: 1200,
      viewportHeight: 800
    };
    
    // Export field state snapshot
    const snapshot = await exporterService.exportFieldStateSnapshot({
      graphId: 'test-graph',
      name: 'Test Snapshot',
      fieldState,
      description: 'Test snapshot for calculator component',
      branch: 'feature/calculator'
    });
    
    // Verify snapshot properties
    expect(snapshot).toHaveProperty('id');
    expect(snapshot).toHaveProperty('name', 'Test Snapshot');
    expect(snapshot).toHaveProperty('description', 'Test snapshot for calculator component');
    expect(snapshot).toHaveProperty('timestamp');
    expect(snapshot).toHaveProperty('graphId', 'test-graph');
    expect(snapshot).toHaveProperty('nodes');
    expect(snapshot).toHaveProperty('edges');
    
    // Verify field state is preserved
    expect(snapshot.fieldState).toMatchObject({
      layout: 'force',
      centerX: 500,
      centerY: 300,
      zoom: 1.2,
      highlightedNodes: ['function_square_root'],
      expandedNodes: ['class_calculator'],
      viewportWidth: 1200,
      viewportHeight: 800
    });
    
    // Verify metadata
    expect(snapshot.metadata).toHaveProperty('branch', 'feature/calculator');
  });
});

/**
 * Mock ConceptGraphService for Exporter tests
 */

// Create a mock conceptGraphService with a clear method
const mockConceptGraphService = {
  conceptGraphs: new Map(),
  
  // Mock clear method
  clear: jest.fn(() => {
    mockConceptGraphService.conceptGraphs.clear();
  }),
  
  // Mock getConceptGraph
  getConceptGraph: jest.fn((graphId = 'default') => {
    return mockConceptGraphService.conceptGraphs.get(graphId) || { nodes: [], edges: [] };
  }),
  
  // Mock getNode method
  getNode: jest.fn((nodeId) => {
    for (const graph of mockConceptGraphService.conceptGraphs.values()) {
      const node = graph.nodes.find(n => n.id === nodeId);
      if (node) return node;
    }
    return null;
  }),
  
  // Mock extractConceptsFromText method
  extractConceptsFromText: jest.fn(async (text) => {
    // Default mock implementation
    const entities = [];
    const relationships = [];
    
    // Extract ALAN-LINEAGE comments
    const lineageMatches = Array.from(text.matchAll(/ALAN-LINEAGE: ([a-z0-9_]+)/g));
    for (const match of lineageMatches) {
      const conceptId = match[1];
      entities.push({
        id: conceptId,
        name: conceptId.split('_').pop(), // Extract name from ID
        type: conceptId.split('_')[0]     // Extract type from ID
      });
    }
    
    return { entities, relationships };
  }),
  
  // Mock addDocument method
  addDocument: jest.fn(async (docId, content, options = {}) => {
    // Mock implementation that extracts concepts from content
    const { entities } = await mockConceptGraphService.extractConceptsFromText(content);
    
    // Add as a new concept graph
    mockConceptGraphService.conceptGraphs.set(docId, {
      id: docId,
      nodes: entities.map(e => ({
        id: e.id,
        label: e.name,
        type: e.type,
        file: docId
      })),
      edges: []
    });
    
    return { success: true };
  }),
  
  // Mock updateFromSource method
  updateFromSource: jest.fn(() => Promise.resolve({
    updatedNodes: [],
    updatedEdges: []
  })),
  
  // Mock subscribe method
  subscribe: jest.fn(() => {
    return () => {}; // Return unsubscribe function
  })
};

export default mockConceptGraphService;

/**
 * ExporterService
 * 
 * Service for exporting concept graphs and other data to various formats
 */
import conceptGraphService from './conceptGraphService';
import { VaultService } from './vaultService';

class ExporterService {
  constructor() {
    this.fileTypes = ['json', 'csv', 'yaml'];
    this.vaultService = new VaultService();
  }

  /**
   * Export concept graph to specified format
   * @param {string} format - The desired export format ('json', 'csv', 'yaml')
   * @param {object} options - Export options
   * @returns {Promise<object>} - The exported data and metadata
   */
  async exportConceptGraph(format = 'json', options = {}) {
    try {
      // Get the current concept graph data
      const conceptGraph = await conceptGraphService.getConceptGraph();
      
      if (!conceptGraph) {
        throw new Error('No concept graph data available');
      }
      
      // Format the data according to requested format
      let formattedData;
      let mimeType;
      
      switch (format.toLowerCase()) {
        case 'json':
          formattedData = JSON.stringify(conceptGraph, null, 2);
          mimeType = 'application/json';
          break;
        case 'csv':
          formattedData = this._convertToCSV(conceptGraph);
          mimeType = 'text/csv';
          break;
        case 'yaml':
          formattedData = this._convertToYAML(conceptGraph);
          mimeType = 'application/x-yaml';
          break;
        default:
          throw new Error(`Unsupported export format: ${format}`);
      }
      
      return {
        data: formattedData,
        mimeType,
        format,
        timestamp: new Date().toISOString(),
        metadata: {
          nodeCount: conceptGraph.nodes?.length || 0,
          linkCount: conceptGraph.links?.length || 0,
          ...options.metadata
        }
      };
    } catch (error) {
      console.error('Error exporting concept graph:', error);
      throw error;
    }
  }

  /**
   * Convert concept graph to CSV format
   * @private
   * @param {object} conceptGraph - The concept graph data
   * @returns {string} - CSV formatted data
   */
  _convertToCSV(conceptGraph) {
    // Simple implementation for testing - in a real app this would be more robust
    const nodes = conceptGraph.nodes || [];
    const nodeHeader = 'id,label,type,phase\n';
    const nodeRows = nodes.map(node => 
      `${node.id},${node.label || ''},${node.type || ''},${node.phase || 0}`
    ).join('\n');
    
    const links = conceptGraph.links || [];
    const linkHeader = '\n\nsource,target,weight\n';
    const linkRows = links.map(link => 
      `${link.source},${link.target},${link.weight || 1}`
    ).join('\n');
    
    return nodeHeader + nodeRows + linkHeader + linkRows;
  }

  /**
   * Convert concept graph to YAML format
   * @private
   * @param {object} conceptGraph - The concept graph data
   * @returns {string} - YAML formatted data
   */
  _convertToYAML(conceptGraph) {
    // Simple YAML conversion for testing
    const yaml = [
      'conceptGraph:',
      '  metadata:',
      `    alpha: ${conceptGraph.alpha || 0}`,
      `    epsilon: ${conceptGraph.epsilon || 0}`,
      '  nodes:'
    ];
    
    const nodes = conceptGraph.nodes || [];
    nodes.forEach(node => {
      yaml.push(`    - id: ${node.id}`);
      yaml.push(`      label: ${node.label || ''}`);
      yaml.push(`      type: ${node.type || ''}`);
      yaml.push(`      phase: ${node.phase || 0}`);
    });
    
    yaml.push('  links:');
    const links = conceptGraph.links || [];
    links.forEach(link => {
      yaml.push(`    - source: ${link.source}`);
      yaml.push(`      target: ${link.target}`);
      yaml.push(`      weight: ${link.weight || 1}`);
    });
    
    return yaml.join('\n');
  }
}

// Export singleton instance
const exporterService = new ExporterService();
export default exporterService;

/**
 * Integration Test for RefactorService + EditorSyncService
 * 
 * Tests the interaction between these services to ensure refactoring operations
 * maintain bidirectional sync between editor and concept graph.
 */

// React not used in this test
import { waitFor, act } from '@testing-library/react';
import refactorService from '../../../services/refactorService';
import conceptGraphService from '../../../services/conceptGraphService';
// Import the mock instead of the real service
import mockEditorSyncService from './mockEditorSyncService';

// Use the mock for all tests
const editorSyncService = mockEditorSyncService;

// Mock CodeMirror editor
class MockCodeMirror {
  constructor() {
    this.value = '';
    this.listeners = {};
    this.selections = [{ anchor: { line: 0, ch: 0 }, head: { line: 0, ch: 0 }}];
    this.markers = [];
    this.gutters = [];
    this.options = {};
  }
  
  on(event, callback) {
    if (!this.listeners[event]) {
      this.listeners[event] = [];
    }
    this.listeners[event].push(callback);
  }
  
  trigger(event, ...args) {
    if (this.listeners[event]) {
      this.listeners[event].forEach(callback => callback(this, ...args));
    }
  }
  
  getValue() {
    return this.value;
  }
  
  setValue(content) {
    this.value = content;
    this.trigger('changes', [
      { from: { line: 0, ch: 0 }, to: { line: 0, ch: 0 }, text: [content] }
    ]);
  }
  
  replaceRange(text, from, to) {
    // Simple text replacement simulation
    this.value = text;
    this.trigger('changes', [
      { from, to, text: [text] }
    ]);
  }
  
  listSelections() {
    return this.selections;
  }
  
  setSelection(from, to) {
    this.selections = [{ anchor: from, head: to }];
    this.trigger('cursorActivity');
  }
  
  markText(from, to, options) {
    const marker = { from, to, options };
    this.markers.push(marker);
    return {
      clear: () => {
        const index = this.markers.indexOf(marker);
        if (index !== -1) {
          this.markers.splice(index, 1);
        }
      }
    };
  }
  
  setOption(name, value) {
    this.options[name] = value;
  }
  
  setGutterMarker(line, gutter, element) {
    this.gutters.push({ line, gutter, element });
  }
  
  scrollIntoView() {
    // Stub implementation
  }
}

