    expect(selector).toHaveClass('top-left');
    
    // Rerender with a different position
    rerender(<PersonaProvider><PersonaSelector position="bottom-right" /></PersonaProvider>);
    expect(selector).toHaveClass('bottom-right');
  });
  
  test('applies custom className', () => {
    renderWithProviders(<PersonaSelector className="custom-class" />);
    
    const selector = screen.getByTestId('persona-selector-container');
    expect(selector).toHaveClass('custom-class');
  });
  
  test('shows keyboard shortcut hint', () => {
    renderWithProviders(<PersonaSelector />);
    
    // Open the menu
    fireEvent.click(screen.getByRole('button', { name: /select persona/i }));
    
    // Check if the keyboard shortcut hint is shown
    expect(screen.getByText(/press/i)).toBeInTheDocument();
    expect(screen.getByText(/alt/i)).toBeInTheDocument();
    expect(screen.getByText(/p/i)).toBeInTheDocument();
    expect(screen.getByText(/panic switch/i)).toBeInTheDocument();
  });
  
  test('has correct accessibility attributes', () => {
    renderWithProviders(<PersonaSelector />);
    
    // Check button accessibility
    const button = screen.getByRole('button', { name: /select persona/i });
    expect(button).toHaveAttribute('aria-expanded', 'false');
    expect(button).toHaveAttribute('aria-haspopup', 'true');
    
    // Open menu and check menu accessibility
    fireEvent.click(button);
    expect(button).toHaveAttribute('aria-expanded', 'true');
    
    const menu = screen.getByRole('menu');
    expect(menu).toHaveAttribute('aria-orientation', 'vertical');
    
    // Check menu items
    const menuItems = screen.getAllByRole('menuitem');
    expect(menuItems.length).toBe(Object.keys(PERSONAS).length);
    
    // Each menu item should have an aria-label
    menuItems.forEach(item => {
      expect(item).toHaveAttribute('aria-label');
    });
  });
});

