    icon: 'architecture',
    panels: ['concept-field', 'editor', 'ripple-preview'],
    features: {
      showCodeEditor: true,
      showConceptGraph: true,
      conceptGraphProminent: true,
      showKoopmanOverlay: true,
      showPhaseColors: true,
      showRippleEffects: true,
    }
  },
  DESIGN_MAKER: {
    id: 'design-maker',
    name: 'Design-First Maker',
