    renderWithProviders(<PersonaSelector />);
    
    // Check if the current persona is displayed
    expect(screen.getByText('Power Coder')).toBeInTheDocument();
  });
  
  test('opens menu when clicked', () => {
    renderWithProviders(<PersonaSelector />);
    
    // Initially, menu should be closed
    expect(screen.queryByRole('menu')).not.toBeInTheDocument();
    
    // Click the button to open the menu
    fireEvent.click(screen.getByRole('button', { name: /select persona/i }));
    
    // Menu should now be open
    const menu = screen.getByRole('menu');
    expect(menu).toBeInTheDocument();
    
    // All personas should be in the menu
    Object.values(PERSONAS).forEach(persona => {
      expect(within(menu).getByText(persona.name)).toBeInTheDocument();
      expect(within(menu).getByText(persona.description)).toBeInTheDocument();
    });
  });
  
  test('closes menu when a persona is selected', () => {
    renderWithProviders(<PersonaSelector />);
    
    // Open the menu
    fireEvent.click(screen.getByRole('button', { name: /select persona/i }));
    expect(screen.getByRole('menu')).toBeInTheDocument();
    
    // Select a different persona
    fireEvent.click(screen.getByRole('menuitem', { name: /switch to concept architect persona/i }));
    
    // Menu should be closed
    expect(screen.queryByRole('menu')).not.toBeInTheDocument();
    
    // Selected persona should be displayed
    expect(screen.getByText('Concept Architect')).toBeInTheDocument();
  });
  
  test('renders with different position props', () => {
    const { rerender } = renderWithProviders(<PersonaSelector position="top-left" />);
    
    // Check if the component has the correct position class
    const selector = screen.getByTestId('persona-selector-container');
    expect(selector).toHaveClass('top-left');
    
    // Rerender with a different position
    rerender(<PersonaProvider><PersonaSelector position="bottom-right" /></PersonaProvider>);
    expect(selector).toHaveClass('bottom-right');
  });
  
  test('applies custom className', () => {
    renderWithProviders(<PersonaSelector className="custom-class" />);
    
    const selector = screen.getByTestId('persona-selector-container');
    expect(selector).toHaveClass('custom-class');
  });
  
  test('shows keyboard shortcut hint', () => {
    renderWithProviders(<PersonaSelector />);
    
    // Open the menu
