import React, { useState } from 'react';
import DemoApp from './DemoApp';
import AffectiveIntegrationExample from './AffectiveIntegrationExample';
import NavigationDemoApp from './NavigationDemoApp';
import AlanIDELayout from './AlanIDELayout';
import SemanticCommitPanel from './components/SemanticCommitPanel';
import './App.css';

/**
 * Main App Component
 * 
 * This is the entry point of the application.
 * It includes five demo applications:
 * - The original DemoApp
 * - The Affective Computing Integration Example
 * - The Navigation & Search Features Demo
 * - The ALAN IDE Full Layout (Wireframe Implementation)
 * - The Semantic Commit Panel (AI-native Version Control)
 */
function App() {
  const [activeDemo, setActiveDemo] = useState('semantic-commit');
  
  const renderActiveDemo = () => {
    switch (activeDemo) {
      case 'original':
        return <DemoApp />;
      case 'affective':
        return <AffectiveIntegrationExample />;
      case 'navigation':
        return <NavigationDemoApp />;
      case 'alan-ide':
        return <AlanIDELayout />;
      case 'semantic-commit':
        return (
          <div className="semantic-commit-container">
            <SemanticCommitPanel />
          </div>
        );
      default:
        return <DemoApp />;
    }
  };
  
  return (
    <div className="app-container">
      <div className="app-switcher">
        <button 
          className={activeDemo === 'semantic-commit' ? 'active' : ''}
          onClick={() => setActiveDemo('semantic-commit')}
        >
          Semantic Commit
        </button>
        <button 
          className={activeDemo === 'alan-ide' ? 'active' : ''}
          onClick={() => setActiveDemo('alan-ide')}
        >
          ALAN IDE Layout
        </button>
        <button 
          className={activeDemo === 'original' ? 'active' : ''}
          onClick={() => setActiveDemo('original')}
        >
          Original Demo
        </button>
        <button 
          className={activeDemo === 'affective' ? 'active' : ''}
          onClick={() => setActiveDemo('affective')}
        >
          Affective Computing
        </button>
        <button 
          className={activeDemo === 'navigation' ? 'active' : ''}
          onClick={() => setActiveDemo('navigation')}
        >
          Navigation & Search
        </button>
      </div>
      
      {renderActiveDemo()}
    </div>
  );
}

export default App;

