Test suite for the configuration utilities to ensure proper functionality.
"""
import os
import sys
import unittest
import tempfile
import yaml
from types import SimpleNamespace

# Add the project root to path so we can import our modules
PROJECT_ROOT = os.path.abspath(os.path.join(os.path.dirname(__file__), '../..'))
if PROJECT_ROOT not in sys.path:
    sys.path.insert(0, PROJECT_ROOT)

from alan2x.utils.config_utils import recursive_namespace, load_config, verify_config, get_nested_attr


class TestConfigUtils(unittest.TestCase):
    """Test cases for the configuration utilities."""

    def test_recursive_namespace_basic(self):
        """Test basic conversion of dictionary to SimpleNamespace."""
        test_dict = {
            'a': 1,
            'b': 'test',
            'c': [1, 2, 3]
        }
        result = recursive_namespace(test_dict)
        self.assertIsInstance(result, SimpleNamespace)
        self.assertEqual(result.a, 1)
        self.assertEqual(result.b, 'test')
        self.assertEqual(result.c, [1, 2, 3])

    def test_recursive_namespace_nested(self):
        """Test conversion of nested dictionaries."""
        test_dict = {
            'a': 1,
            'paths': {
                'concept_index': 'data/index.npz',
                'logs': 'logs/'
            }
        }
        result = recursive_namespace(test_dict)
        self.assertIsInstance(result, SimpleNamespace)
        self.assertIsInstance(result.paths, SimpleNamespace)
        self.assertEqual(result.paths.concept_index, 'data/index.npz')
        self.assertEqual(result.paths.logs, 'logs/')

    def test_recursive_namespace_lists(self):
        """Test conversion with lists of dictionaries."""
        test_dict = {
            'items': [
                {'id': 1, 'name': 'item1'},
                {'id': 2, 'name': 'item2'}
            ]
        }
        result = recursive_namespace(test_dict)
        self.assertIsInstance(result, SimpleNamespace)
        self.assertEqual(len(result.items), 2)
        self.assertIsInstance(result.items[0], SimpleNamespace)
        self.assertEqual(result.items[0].id, 1)
        self.assertEqual(result.items[1].name, 'item2')

    def test_load_config(self):
        """Test loading config from a YAML file."""
        config_data = {
            'paths': {
                'concept_index': 'test/path.npz'
            },
            'partitioning': {
                'sim_threshold': 0.75
            }
        }

        # Create a temporary YAML file
        with tempfile.NamedTemporaryFile(mode='w', suffix='.yaml', delete=False) as temp:
            yaml.dump(config_data, temp)
            temp_path = temp.name

        try:
            # Test loading the config
            config = load_config(temp_path)
            self.assertIsInstance(config, SimpleNamespace)
            self.assertIsInstance(config.paths, SimpleNamespace)
            self.assertEqual(config.paths.concept_index, 'test/path.npz')
            self.assertEqual(config.partitioning.sim_threshold, 0.75)

            # Test with default values
            default_config = {
                'device': 'cpu',
                'paths': {
                    'other_path': 'default/path'
                }
            }
            config = load_config(temp_path, default_config)
            self.assertEqual(config.device, 'cpu')  # From default
            self.assertEqual(config.paths.concept_index, 'test/path.npz')  # From file
            self.assertEqual(config.paths.other_path, 'default/path')  # From default
        finally:
            # Clean up
            os.unlink(temp_path)

    def test_verify_config(self):
        """Test verification of required config keys."""
        config = recursive_namespace({
            'paths': {
                'concept_index': 'path/to/index.npz'
            },
            'device': 'cpu'
        })

        # Should pass with all required keys present
        self.assertTrue(verify_config(config, ['paths.concept_index', 'device']))

        # Should fail with missing keys
        self.assertFalse(verify_config(config, ['paths.concept_index', 'missing_key']))
        self.assertFalse(verify_config(config, ['paths.missing_nested']))

    def test_get_nested_attr(self):
        """Test safely getting nested attributes."""
        config = recursive_namespace({
            'paths': {
                'concept_index': 'path/to/index.npz'
            },
            'device': 'cpu'
        })

        # Get existing attributes
        self.assertEqual(get_nested_attr(config, 'device'), 'cpu')
        self.assertEqual(get_nested_attr(config, 'paths.concept_index'), 'path/to/index.npz')

        # Get non-existing attributes with default
        self.assertEqual(get_nested_attr(config, 'missing', 'default'), 'default')
        self.assertEqual(get_nested_attr(config, 'paths.missing', 'default'), 'default')


if __name__ == '__main__':
    unittest.main()
