import { VaultService } from './vaultService';

class RefactorService {
  constructor() {
    this.vaultService = new VaultService();
    this.transformations = new Map();
    this.secretPatterns = [
      {
        name: 'API Key',
        regex: /(?:api[_-]?key|apikey)['":\s]*=\s*['"]([a-zA-Z0-9_-]{16,64})['"]/gi,
        replacement: (match, key) => this.createVaultReference('API_KEY', key)
      },
      {
        name: 'Access Token',
        regex: /(?:access[_-]?token|token)['":\s]*=\s*['"]([a-zA-Z0-9_\-\.]{16,64})['"]/gi,
        replacement: (match, token) => this.createVaultReference('ACCESS_TOKEN', token)
      },
      {
        name: 'Password',
        regex: /(?:password|passwd|pwd)['":\s]*=\s*['"]([^'"]{8,64})['"]/gi,
        replacement: (match, pwd) => this.createVaultReference('PASSWORD', pwd)
      },
      {
        name: 'Connection String',
        regex: /(["'])(?:mongodb|postgres|mysql|jdbc|redis):\/\/(?:[^:]+:[^@]+@)?[^"']+\1/gi,
        replacement: (match, quote, connString) => this.createVaultReference('CONNECTION_STRING', connString)
      }
    ];
    
    // Register built-in transformations
    this.registerTransformations();
  }
  
  /**
   * Register built-in code transformations.
   */
  registerTransformations() {
    // Extract Function transformation
    this.registerTransformation('extractFunction', {
      name: 'Extract Function',
      description: 'Extract selected code into a new function',
      appliesTo: ['function', 'method', 'code_block'],
      applyTransform: this.extractFunction.bind(this)
    });
    
    // Inline Function transformation
    this.registerTransformation('inlineFunction', {
      name: 'Inline Function',
      description: 'Inline a function at its call sites',
      appliesTo: ['function', 'method'],
      applyTransform: this.inlineFunction.bind(this)
    });
    
    // Rename Symbol transformation
    this.registerTransformation('renameSymbol', {
      name: 'Rename Symbol',
      description: 'Rename a variable, function, class, or other symbol',
      appliesTo: ['function', 'method', 'class', 'variable', 'parameter'],
      applyTransform: this.renameSymbol.bind(this)
    });
    
    // Secure Secrets transformation
    this.registerTransformation('secureSecrets', {
      name: 'Secure Secrets',
      description: 'Replace hardcoded secrets with vault references',
      appliesTo: ['module', 'function', 'class', 'variable'],
      applyTransform: this.secureSecrets.bind(this)
    });
    
    // Add Type Annotation transformation
    this.registerTransformation('addTypeAnnotation', {
      name: 'Add Type Annotation',
      description: 'Add type annotations to functions and variables',
      appliesTo: ['function', 'method', 'parameter', 'variable'],
      applyTransform: this.addTypeAnnotation.bind(this)
    });
    
    // Convert to Modern JS transformation
    this.registerTransformation('convertToModernJS', {
      name: 'Modernize JavaScript',
      description: 'Convert to modern JavaScript (ES6+) syntax',
      appliesTo: ['module', 'function', 'variable'],
      applyTransform: this.convertToModernJS.bind(this)
    });
    
    // Convert Function to Method transformation
    this.registerTransformation('convertFunctionToMethod', {
      name: 'Convert Function to Method',
      description: 'Convert a function to a class method',
      appliesTo: ['function'],
      applyTransform: this.convertFunctionToMethod.bind(this)
    });
  }
  
  /**
   * Register a new code transformation.
   * 
   * @param {string} id - Unique identifier for the transformation
   * @param {Object} transformation - Transformation definition
   * @param {string} transformation.name - Display name
   * @param {string} transformation.description - Description
   * @param {Array<string>} transformation.appliesTo - Node types the transformation applies to
   * @param {Function} transformation.applyTransform - Function to apply the transformation
   */
  registerTransformation(id, transformation) {
    if (this.transformations.has(id)) {
      console.warn(`Transformation with ID ${id} already exists. Overwriting.`);
    }
    
    this.transformations.set(id, transformation);
  }
  
  /**
   * Get all available transformations.
   * 
   * @returns {Array} Array of transformation objects
   */
  getTransformations() {
    return Array.from(this.transformations.entries()).map(([id, transform]) => ({
      id,
      ...transform
    }));
  }
  
  /**
   * Get transformations applicable to a specific node type.
   * 
   * @param {string} nodeType - Type of node
   * @returns {Array} Array of applicable transformation objects
   */
  getApplicableTransformations(nodeType) {
    return this.getTransformations().filter(transform => 
      transform.appliesTo.includes(nodeType)
    );
  }
  
  /**
   * Apply a transformation to nodes.
   * 
   * @param {string} transformationId - ID of the transformation to apply
   * @param {Array|string} nodeIds - Node IDs to transform
   * @param {Object} options - Options for the transformation
   * @returns {Promise<Object>} Result of the transformation
   */
  applyTransformation(transformationId, nodeIds, options = {}) {
    const transformation = this.transformations.get(transformationId);
    
    if (!transformation) {
      return Promise.reject(new Error(`Transformation ${transformationId} not found`));
    }
    
    // Convert single ID to array
    const ids = Array.isArray(nodeIds) ? nodeIds : [nodeIds];
    
    // Get nodes from concept graph
    const nodes = ids.map(id => conceptGraphService.getNode(id)).filter(node => !!node);
    
    if (nodes.length === 0) {
      return Promise.reject(new Error('No valid nodes found for transformation'));
    }
    
    // Check if transformation applies to these nodes
    const invalidNodes = nodes.filter(node => 
      !transformation.appliesTo.includes(node.type)
    );
    
    if (invalidNodes.length > 0) {
      return Promise.reject(new Error(
        `Transformation ${transformationId} doesn't apply to nodes: ${invalidNodes.map(n => n.id).join(', ')}`
      ));
    }
    
    // Apply the transformation
    return transformation.applyTransform(nodes, options);
  }
  
  /**
   * Extract Function transformation implementation.
   * 
   * @param {Array} nodes - Nodes to transform
   * @param {Object} options - Options for extraction
   * @param {string} options.functionName - Name for the extracted function
   * @param {Array<string>} options.parameters - Parameters for the function
   * @param {string} options.returnType - Return type for the function
   * @returns {Promise<Object>} Transformation result
   */
  extractFunction(nodes, options = {}) {
    // In a real implementation, this would perform AST manipulation
    // This is a simplified placeholder that returns the transformation plan
    
    const { functionName = 'extractedFunction', parameters = [], returnType = '' } = options;
    
    // Build parameter list
    const paramList = parameters.map(param => {
      if (returnType) {
        return `${param}: any`; // Simplified type annotation
      }
      return param;
    }).join(', ');
    
    // Build function signature
    let functionSignature = `function ${functionName}(${paramList})`;
    if (returnType) {
      functionSignature += `: ${returnType}`;
    }
    
    // Create transformation plan
    const transformationPlan = {
      type: 'extractFunction',
      sourceNodes: nodes.map(node => node.id),
      createdNodes: [
        {
          id: `function_${functionName}`,
          type: 'function',
          label: functionName,
          signature: functionSignature
        }
      ],
      changes: [
        {
          type: 'replace',
          nodeId: nodes[0].id,
          oldText: '[Original code block]', // This would be determined by analyzing the AST
          newText: `${functionName}(${parameters.join(', ')})`, // Function call
          description: `Replace code with call to ${functionName}`
        },
        {
          type: 'insert',
          position: 'beforeNode', // This could be relative to different nodes
          referenceNodeId: nodes[0].id,
          text: `${functionSignature} {\n  // Extracted code\n}`,
          description: `Insert ${functionName} function definition`
        }
      ]
    };
    
    return Promise.resolve({
      success: true,
      transformationPlan
    });
  }
  
  /**
   * Inline Function transformation implementation.
   * 
   * @param {Array} nodes - Nodes to transform (functions to inline)
   * @param {Object} options - Options for inlining
   * @returns {Promise<Object>} Transformation result
   */
  inlineFunction(nodes, options = {}) {
    // In a real implementation, this would perform AST manipulation
    // This is a simplified placeholder that returns the transformation plan
    
    // Create transformation plan
    const transformationPlan = {
      type: 'inlineFunction',
      sourceNodes: nodes.map(node => node.id),
      changes: []
    };
    
    // For each function to inline
    nodes.forEach(functionNode => {
      // Find call sites (would require AST analysis)
      const callSites = []; // Placeholder for call sites
      
      // Add replacements for each call site
      callSites.forEach(callSite => {
        transformationPlan.changes.push({
          type: 'replace',
          nodeId: callSite.id,
          oldText: `${functionNode.label}(...)`, // Function call
          newText: '/* Inlined function body */',
          description: `Inline function ${functionNode.label}`
        });
      });
      
      // Remove function definition
      transformationPlan.changes.push({
        type: 'delete',
        nodeId: functionNode.id,
        description: `Remove ${functionNode.label} function definition`
      });
    });
    
    return Promise.resolve({
      success: true,
      transformationPlan
    });
  }
  
  /**
   * Rename Symbol transformation implementation.
   * 
   * @param {Array} nodes - Nodes to transform (symbols to rename)
   * @param {Object} options - Options for renaming
   * @param {string} options.newName - New name for the symbol
   * @returns {Promise<Object>} Transformation result
   */
  renameSymbol(nodes, options = {}) {
    const { newName } = options;
    
    if (!newName) {
      return Promise.reject(new Error('New name not provided'));
    }
    
    // Create transformation plan
    const transformationPlan = {
      type: 'renameSymbol',
      sourceNodes: nodes.map(node => node.id),
      changes: []
    };
    
    // For each symbol to rename
    nodes.forEach(symbolNode => {
      // Find all references (would require AST analysis)
      const references = []; // Placeholder for references
      
      // Add changes for the symbol definition
      transformationPlan.changes.push({
        type: 'replace',
        nodeId: symbolNode.id,
        oldText: symbolNode.label,
        newText: newName,
        description: `Rename ${symbolNode.label} to ${newName} (definition)`
      });
      
      // Add changes for each reference
      references.forEach(ref => {
        transformationPlan.changes.push({
          type: 'replace',
          nodeId: ref.id,
          oldText: symbolNode.label,
          newText: newName,
          description: `Rename ${symbolNode.label} to ${newName} (reference)`
        });
      });
    });
    
    return Promise.resolve({
      success: true,
      transformationPlan
    });
  }
  
  /**
   * Secure Secrets transformation implementation.
   * 
   * @param {Array} nodes - Nodes to transform
   * @param {Object} options - Options for securing secrets
   * @returns {Promise<Object>} Transformation result
   */
  secureSecrets(nodes, options = {}) {
    // Create transformation plan
    const transformationPlan = {
      type: 'secureSecrets',
      sourceNodes: nodes.map(node => node.id),
      changes: [],
      secretsFound: []
    };
    
    // For each node to scan
    nodes.forEach(node => {
      // Get the source code (would require AST or source mapping)
      const sourceCode = node.metadata?.sourceText || '';
      
      // Scan for secrets using patterns
      this.secretPatterns.forEach(pattern => {
        const regex = new RegExp(pattern.regex);
        let match;
        
        // Find all matches in the source code
        while ((match = regex.exec(sourceCode)) !== null) {
          const secret = match[1];
          const fullMatch = match[0];
          const startIndex = match.index;
          const endIndex = startIndex + fullMatch.length;
          
          // Create a vault key for this secret
          const vaultKey = this.generateVaultKey(pattern.name, node.label);
          
          // Add to secrets found
          transformationPlan.secretsFound.push({
            type: pattern.name,
            nodeId: node.id,
            vaultKey
          });
          
          // Create replacement
          const replacement = pattern.replacement(fullMatch, secret);
          
          // Add change to replace the secret
          transformationPlan.changes.push({
            type: 'replace',
            nodeId: node.id,
            range: { startIndex, endIndex },
            oldText: fullMatch,
            newText: replacement,
            description: `Replace ${pattern.name} with vault reference`
          });
        }
      });
    });
    
    return Promise.resolve({
      success: true,
      transformationPlan
    });
  }
  
  /**
   * Add Type Annotation transformation implementation.
   * 
   * @param {Array} nodes - Nodes to transform
   * @param {Object} options - Options for adding type annotations
   * @returns {Promise<Object>} Transformation result
   */
  addTypeAnnotation(nodes, options = {}) {
    // This would use type inference in a real implementation
    // Here we'll just provide placeholder type annotations
    
    // Create transformation plan
    const transformationPlan = {
      type: 'addTypeAnnotation',
      sourceNodes: nodes.map(node => node.id),
      changes: []
    };
    
    // Type annotation mapping by node type
    const typeMapping = {
      function: 'any',
      method: 'any',
      parameter: 'any',
      variable: 'any'
    };
    
    // For each node to annotate
    nodes.forEach(node => {
      const inferredType = options[node.id]?.type || typeMapping[node.type] || 'any';
      
      // Language-specific annotations
      if (node.metadata?.language === 'python') {
        if (node.type === 'function' || node.type === 'method') {
          transformationPlan.changes.push({
            type: 'replace',
            nodeId: node.id,
            description: `Add return type annotation to ${node.label}`,
            oldText: `)`, // End of parameter list
            newText: `) -> ${inferredType}`, // Add return type
          });
        } else if (node.type === 'parameter' || node.type === 'variable') {
          transformationPlan.changes.push({
            type: 'replace',
            nodeId: node.id,
            description: `Add type annotation to ${node.label}`,
            oldText: node.label,
            newText: `${node.label}: ${inferredType}`,
          });
        }
      } else {
        // TypeScript-style annotations
        if (node.type === 'function' || node.type === 'method') {
          transformationPlan.changes.push({
            type: 'replace',
            nodeId: node.id,
            description: `Add return type annotation to ${node.label}`,
            oldText: `)`, // End of parameter list
            newText: `): ${inferredType}`, // Add return type
          });
        } else if (node.type === 'parameter' || node.type === 'variable') {
          transformationPlan.changes.push({
            type: 'replace',
            nodeId: node.id,
            description: `Add type annotation to ${node.label}`,
            oldText: node.label,
            newText: `${node.label}: ${inferredType}`,
          });
        }
      }
    });
    
    return Promise.resolve({
      success: true,
      transformationPlan
    });
  }
  
  /**
   * Convert to Modern JS transformation implementation.
   * 
   * @param {Array} nodes - Nodes to transform
   * @param {Object} options - Options for modernization
   * @returns {Promise<Object>} Transformation result
   */
  convertToModernJS(nodes, options = {}) {
    // Create transformation plan
    const transformationPlan = {
      type: 'convertToModernJS',
      sourceNodes: nodes.map(node => node.id),
      changes: []
    };
    
    // For each node to modernize
    nodes.forEach(node => {
      // This would involve complex AST analysis in a real implementation
      // Here we'll just provide example transformations
      
      if (node.type === 'function') {
        // Convert function declaration to arrow function
        transformationPlan.changes.push({
          type: 'replace',
          nodeId: node.id,
          oldText: `function ${node.label}(...)`,
          newText: `const ${node.label} = (...) =>`,
          description: `Convert ${node.label} to arrow function`
        });
      } else if (node.type === 'variable') {
        // Convert var to const/let
        transformationPlan.changes.push({
          type: 'replace',
          nodeId: node.id,
          oldText: `var ${node.label}`,
          newText: `const ${node.label}`,
          description: `Convert var to const for ${node.label}`
        });
      }
    });
    
    return Promise.resolve({
      success: true,
      transformationPlan
    });
  }
  
  /**
   * Convert Function to Method transformation implementation.
   * 
   * @param {Array} nodes - Nodes to transform (functions to convert)
   * @param {Object} options - Options for conversion
   * @param {string} options.className - Name of the class to add the method to
   * @returns {Promise<Object>} Transformation result
   */
  convertFunctionToMethod(nodes, options = {}) {
    const { className } = options;
    
    if (!className) {
      return Promise.reject(new Error('Class name not provided'));
    }
    
    // Create transformation plan
    const transformationPlan = {
      type: 'convertFunctionToMethod',
      sourceNodes: nodes.map(node => node.id),
      changes: []
    };
    
    // For each function to convert
    nodes.forEach(functionNode => {
      // Delete the original function
      transformationPlan.changes.push({
        type: 'delete',
        nodeId: functionNode.id,
        description: `Remove ${functionNode.label} function`
      });
      
      // Add as method to class
      transformationPlan.changes.push({
        type: 'insert',
        targetNodeId: `class_${className}`, // Assuming this is how class nodes are identified
        position: 'asChild',
        text: `  ${functionNode.label}(...) {\n    // Method body\n  }`,
        description: `Add ${functionNode.label} as method to ${className} class`
      });
    });
    
    return Promise.resolve({
      success: true,
      transformationPlan
    });
  }
  
  /**
   * Create a reference to a vault secret.
   * 
   * @param {string} secretType - Type of secret
   * @param {string} secretValue - Value of the secret
   * @returns {string} Vault reference code
   */
  createVaultReference(secretType, secretValue) {
    // Generate a vault key
    const vaultKey = this.generateVaultKey(secretType);
    
    // Store in vault if possible
    try {
      this.vaultService.storeSecret(vaultKey, secretValue);
    } catch (err) {
      console.warn('Could not store secret in vault:', err);
    }
    
    // Create a reference based on language
    return `process.env.${vaultKey} /* Vault: ${secretType} */`;
  }
  
  /**
   * Generate a unique vault key for a secret.
   * 
   * @param {string} secretType - Type of secret
   * @param {string} context - Additional context for the key
   * @returns {string} Generated vault key
   */
  generateVaultKey(secretType, context = '') {
    const prefix = secretType.toUpperCase().replace(/[^A-Z0-9]/g, '_');
    const suffix = context ? `_${context.toUpperCase().replace(/[^A-Z0-9]/g, '_')}` : '';
    const unique = Math.floor(Math.random() * 1000).toString().padStart(3, '0');
    
    return `${prefix}${suffix}_${unique}`;
  }
  
  /**
   * Run the Secret Lint tool to identify and fix secrets.
   * 
   * @param {string} sourceCode - Code to analyze
   * @param {Object} options - Options for the linter
   * @returns {Promise<Object>} Lint results and fixes
   */
  runSecretLint(sourceCode, options = {}) {
    const results = {
      secrets: [],
      fixes: []
    };
    
    // Scan for secrets using patterns
    this.secretPatterns.forEach(pattern => {
      const regex = new RegExp(pattern.regex);
      let match;
      
      // Find all matches in the source code
      while ((match = regex.exec(sourceCode)) !== null) {
        const secret = match[1];
        const fullMatch = match[0];
        const startIndex = match.index;
        const endIndex = startIndex + fullMatch.length;
        
        // Create a vault key for this secret
        const vaultKey = this.generateVaultKey(pattern.name);
        
        // Add to secrets found
        results.secrets.push({
          type: pattern.name,
          startIndex,
          endIndex,
          vaultKey
        });
        
        // Create replacement
        const replacement = pattern.replacement(fullMatch, secret);
        
        // Add fix
        results.fixes.push({
          startIndex,
          endIndex,
          replacement,
          description: `Replace ${pattern.name} with vault reference`
        });
      }
    });
    
    return Promise.resolve(results);
  }
  
  /**
   * Apply fixes from Secret Lint to source code.
   * 
   * @param {string} sourceCode - Original source code
   * @param {Array} fixes - Fixes to apply
   * @returns {string} Updated source code
   */
  applySecretLintFixes(sourceCode, fixes) {
    // Sort fixes in reverse order to avoid shifting indices
    const sortedFixes = [...fixes].sort((a, b) => b.startIndex - a.startIndex);
    
    // Apply fixes one by one
    let updatedCode = sourceCode;
    for (const fix of sortedFixes) {
      const { startIndex, endIndex, replacement } = fix;
      updatedCode = 
        updatedCode.substring(0, startIndex) + 
        replacement + 
        updatedCode.substring(endIndex);
    }
    
    return updatedCode;
  }
}

// Create singleton instance
const refactorService = new RefactorService();

export default refactorService;

/**
 * Editor Sync Service
 * 
 * Manages bidirectional synchronization between the CodeMirror editor and concept graph.
 * This enables real-time connection between text-based editing and the visual
 * concept field representation.
 * 
 * Part of Sprint 2 - Phase 3 ALAN IDE implementation.
 */

import conceptGraphService from './conceptGraphService';
import { debounce } from '../utils/performance';

class EditorSyncService {
  constructor() {
    this.editor = null;
    this.conceptFieldCanvas = null;
    this.isProcessingEditorChange = false;
    this.isProcessingConceptChange = false;
    this.positionMap = new Map(); // Maps between editor positions and concept node IDs
    this.listeners = {
      editorChange: [],
      conceptChange: [],
      selectionChange: [],
    };
    
    // Debounced handlers for performance
    this.handleEditorChangeDebounced = debounce(this.handleEditorChange.bind(this), 300);
    this.handleConceptChangeDebounced = debounce(this.handleConceptChange.bind(this), 300);
  }

  /**
   * Initialize the synchronization service.
   * 
   * @param {Object} editor - CodeMirror editor instance
   * @param {Object} conceptFieldCanvas - ConceptFieldCanvas React component reference
   */
  initialize(editor, conceptFieldCanvas) {
    this.editor = editor;
    this.conceptFieldCanvas = conceptFieldCanvas;
    
    if (!editor || !conceptFieldCanvas) {
      console.error('EditorSyncService: Both editor and conceptFieldCanvas must be provided');
      return;
    }
    
    // Set up editor event listeners
    this.setupEditorListeners();
    
    // Set up concept graph event listeners
    this.setupConceptGraphListeners();
    
    // Initial position mapping based on the current state
    this.buildPositionMap();
    
    console.info('EditorSyncService: Bidirectional sync initialized');
  }
  
  /**
   * Set up listeners for CodeMirror editor events.
   */
  setupEditorListeners() {
    if (!this.editor) return;
    
    // CodeMirror events
    this.editor.on('changes', (instance, changes) => {
      if (!this.isProcessingConceptChange) {
        this.handleEditorChangeDebounced(changes);
      }
    });
    
    this.editor.on('cursorActivity', instance => {
      if (!this.isProcessingConceptChange) {
        const selection = instance.listSelections()[0];
        this.handleEditorSelectionChange(selection);
      }
    });
    
    // Listen for viewport changes to optimize rendering
    this.editor.on('viewportChange', (instance, from, to) => {
      // Update visible concept nodes based on viewport
      this.updateVisibleNodes(from, to);
    });
  }
  
  /**
   * Set up listeners for concept graph events.
   */
  setupConceptGraphListeners() {
    // Subscribe to concept graph changes
    conceptGraphService.subscribe('graphUpdated', update => {
      if (!this.isProcessingEditorChange) {
        this.handleConceptChangeDebounced(update);
      }
    });
    
    // Setup handler for node selection in the concept graph
    if (this.conceptFieldCanvas && this.conceptFieldCanvas.onNodeSelect) {
      const originalHandler = this.conceptFieldCanvas.onNodeSelect;
      
      this.conceptFieldCanvas.onNodeSelect = (nodeIds) => {
        // First, call the original handler
        originalHandler(nodeIds);
        
        // Then handle the selection for editor sync
        if (!this.isProcessingEditorChange) {
          this.handleConceptSelectionChange(nodeIds);
        }
      };
    }
  }
  
  /**
   * Build mapping between editor positions and concept nodes.
   * This creates a bidirectional map that allows quick lookup
   * in either direction during sync operations.
   */
  buildPositionMap() {
    this.positionMap.clear();
    
    // Get the current concept graph
    const graph = conceptGraphService.getConceptGraph();
    if (!graph || !graph.nodes) return;
    
    // Create a map from node ID to editor position
    // This is a simplified implementation - the real one would need to
    // parse the source code and match it with the concept graph
    graph.nodes.forEach(node => {
      if (node.metadata && node.metadata.sourcePosition) {
        const { line, ch, endLine, endCh } = node.metadata.sourcePosition;
        
        // Store the position mapping
        this.positionMap.set(node.id, { line, ch, endLine, endCh });
      }
    });
    
    console.debug(`EditorSyncService: Built position map with ${this.positionMap.size} nodes`);
  }
  
  /**
   * Handle changes in the editor content and propagate to concept graph.
   * 
   * @param {Array} changes - Array of change objects from CodeMirror
   */
  handleEditorChange(changes) {
    this.isProcessingEditorChange = true;
    
    try {
      // Get current editor content
      const content = this.getEditorContent();
      
      // Update concept graph based on editor changes
      // This would involve parsing the code and updating the concept graph
      conceptGraphService.updateFromSource(content)
        .then(({ updatedNodes, updatedEdges }) => {
          console.debug(`EditorSyncService: Updated ${updatedNodes.length} nodes and ${updatedEdges.length} edges from editor change`);
          
          // Rebuild position map after update
          this.buildPositionMap();
          
          // Notify listeners
          this.notifyListeners('editorChange', { 
            changes, 
            updatedNodes, 
            updatedEdges 
          });
        })
        .catch(err => {
          console.error('EditorSyncService: Error updating concept graph', err);
        });
    } finally {
      this.isProcessingEditorChange = false;
    }
  }
  
  /**
   * Handle selection changes in the editor and update concept graph selection.
   * 
   * @param {Object} selection - Selection from CodeMirror editor
   */
  handleEditorSelectionChange(selection) {
    if (this.isProcessingConceptChange) return;
    
    // Convert editor selection to node IDs
    const nodeIds = this.findNodesInRange(selection);
    
    // Update selection in concept field canvas
    if (this.conceptFieldCanvas && this.conceptFieldCanvas.selectNodes) {
      this.conceptFieldCanvas.selectNodes(nodeIds);
    }
    
    // Notify listeners
    this.notifyListeners('selectionChange', { 
      source: 'editor', 
      selection, 
      nodeIds 
    });
  }
  
  /**
   * Handle changes in the concept graph and propagate to editor.
   * 
   * @param {Object} update - Update event from the concept graph
   */
  handleConceptChange(update) {
    this.isProcessingConceptChange = true;
    
    try {
      const { changedNodes, changedEdges } = update;
      
      // Generate text changes for the editor based on concept graph changes
      if (changedNodes && changedNodes.length > 0) {
        // For each changed node, find its position in the editor
        // and update the editor content
        changedNodes.forEach(node => {
          const position = this.positionMap.get(node.id);
          if (position && node.metadata && node.metadata.sourceText) {
            // Replace the text in the editor
            this.replaceEditorText(position, node.metadata.sourceText);
          }
        });
      }
      
      // Notify listeners
      this.notifyListeners('conceptChange', update);
    } finally {
      this.isProcessingConceptChange = false;
    }
  }
  
  /**
   * Handle selection changes in the concept graph and update editor selection.
   * 
   * @param {Array} nodeIds - Selected node IDs
   */
  handleConceptSelectionChange(nodeIds) {
    if (this.isProcessingEditorChange) return;
    
    // Find editor positions for the selected nodes
    const positions = this.findPositionsForNodes(nodeIds);
    
    // Update selection in editor
    this.setEditorSelection(positions);
    
    // Notify listeners
    this.notifyListeners('selectionChange', { 
      source: 'concept', 
      nodeIds, 
      positions 
    });
  }
  
  /**
   * Replace text in the editor at the specified position.
   * 
   * @param {Object} position - Position with line/ch info
   * @param {string} newText - New text to insert
   */
  replaceEditorText(position, newText) {
    if (!this.editor) return;
    
    const { line, ch, endLine, endCh } = position;
    
    // Use CodeMirror's replaceRange method
    this.editor.replaceRange(
      newText,
      { line, ch },
      { line: endLine, ch: endCh }
    );
  }
  
  /**
   * Set selection in the editor based on positions.
   * 
   * @param {Array} positions - Array of position ranges
   */
  setEditorSelection(positions) {
    if (!this.editor || positions.length === 0) return;
    
    // Get the first position
    const position = positions[0];
    
    // Set cursor or selection in CodeMirror
    this.editor.setSelection(
      { line: position.line, ch: position.ch },
      { line: position.endLine, ch: position.endCh }
    );
    
    // Scroll into view
    this.editor.scrollIntoView(null, 100);
  }
  
  /**
   * Find concept nodes that fall within an editor selection range.
   * 
   * @param {Object} selection - CodeMirror selection
   * @returns {Array} Array of node IDs
   */
  findNodesInRange(selection) {
    // This is a simplified implementation
    // A real implementation would use the position map to find
    // nodes that fall within the selection range
    const nodeIds = [];
    
    // CodeMirror selection has anchor and head properties
    // which determine the start and end of the selection
    const start = selection.anchor;
    const end = selection.head;
    
    // Ensure start is before end
    const rangeStart = start.line < end.line || (start.line === end.line && start.ch <= end.ch) 
      ? start : end;
    const rangeEnd = start.line < end.line || (start.line === end.line && start.ch <= end.ch) 
      ? end : start;
    
    this.positionMap.forEach((position, nodeId) => {
      // Check if node position intersects with the selection
      const isInRange = this.checkPositionInRange(position, { start: rangeStart, end: rangeEnd });
      if (isInRange) {
        nodeIds.push(nodeId);
      }
    });
    
    return nodeIds;
  }
  
  /**
   * Find editor positions for a set of concept node IDs.
   * 
   * @param {Array} nodeIds - Array of node IDs
   * @returns {Array} Array of position ranges
   */
  findPositionsForNodes(nodeIds) {
    const positions = [];
    
    nodeIds.forEach(nodeId => {
      const position = this.positionMap.get(nodeId);
      if (position) {
        positions.push(position);
      }
    });
    
    return positions;
  }
  
  /**
   * Helper method to check if a position is within a selection range.
   * 
   * @param {Object} position - Node position with line/ch info
   * @param {Object} range - Range with start and end positions
   * @returns {boolean} True if position is in range
   */
  checkPositionInRange(position, range) {
    // CodeMirror-specific range check
    const { start, end } = range;
    
    // Check if any part of the position is within the range
    return (
      // Start point is in range
      ((position.line > start.line || (position.line === start.line && position.ch >= start.ch)) &&
       (position.line < end.line || (position.line === end.line && position.ch <= end.ch))) ||
      // End point is in range
      ((position.endLine > start.line || (position.endLine === start.line && position.endCh >= start.ch)) &&
       (position.endLine < end.line || (position.endLine === end.line && position.endCh <= end.ch))) ||
      // Range is contained within position
      (position.line <= start.line && position.endLine >= end.line)
    );
  }
  
  /**
   * Update visible nodes based on editor viewport.
   * 
   * @param {number} from - Starting line in viewport
   * @param {number} to - Ending line in viewport
   */
  updateVisibleNodes(from, to) {
    // Find nodes that are visible in the current viewport
    const visibleNodeIds = [];
    
    this.positionMap.forEach((position, nodeId) => {
      if (position.line <= to && position.endLine >= from) {
        visibleNodeIds.push(nodeId);
      }
    });
    
    // Update visibility in concept field canvas
    if (this.conceptFieldCanvas && this.conceptFieldCanvas.setVisibleNodes) {
      this.conceptFieldCanvas.setVisibleNodes(visibleNodeIds);
    }
  }
  
  /**
   * Get the current content from the CodeMirror editor.
   * 
   * @returns {string} Editor content
   */
  getEditorContent() {
    return this.editor ? this.editor.getValue() : '';
  }
  
  /**
   * Subscribe to events from the sync service.
   * 
   * @param {string} event - Event type: 'editorChange', 'conceptChange', 'selectionChange'
   * @param {Function} callback - Callback function
   * @returns {Function} Unsubscribe function
   */
  subscribe(event, callback) {
    if (!this.listeners[event]) {
      this.listeners[event] = [];
    }
    
    this.listeners[event].push(callback);
    
    // Return unsubscribe function
    return () => {
      this.listeners[event] = this.listeners[event].filter(cb => cb !== callback);
    };
  }
  
  /**
   * Notify listeners of an event.
   * 
   * @param {string} event - Event type
   * @param {Object} data - Event data
   */
  notifyListeners(event, data) {
    if (!this.listeners[event]) return;
    
    this.listeners[event].forEach(callback => {
      try {
        callback(data);
      } catch (err) {
        console.error(`EditorSyncService: Error in listener for ${event}`, err);
      }
    });
  }
  
  /**
   * Get mapping from node ID to editor position.
   * 
   * @returns {Map} Map of node IDs to positions
   */
  getPositionMap() {
    return new Map(this.positionMap);
  }
  
  /**
   * Highlight a concept node in the editor.
   * 
   * @param {string} nodeId - Node ID to highlight
   * @param {string} className - CSS class for highlighting
   * @returns {Object} Marker object that can be cleared later
   */
  highlightNode(nodeId, className = 'highlighted-concept') {
    if (!this.editor) return null;
    
    const position = this.positionMap.get(nodeId);
    if (!position) return null;
    
    // Create a marker in the editor
    return this.editor.markText(
      { line: position.line, ch: position.ch },
      { line: position.endLine, ch: position.endCh },
      { className }
    );
  }
  
  /**
   * Create concept gutters in the editor.
   * 
   * @param {Object} options - Options for gutter
   */
  createConceptGutters(options = {}) {
    if (!this.editor) return;
    
    // Add a gutter to the editor
    this.editor.setOption('gutters', ['CodeMirror-linenumbers', 'concept-gutter']);
    
    // Process each node to add gutter markers
    this.positionMap.forEach((position, nodeId) => {
      const node = conceptGraphService.getNode(nodeId);
      if (!node) return;
      
      // Create gutter element
      const marker = document.createElement('div');
      marker.className = `concept-marker concept-type-${node.type}`;
      marker.innerHTML = 'â—';
      marker.title = node.label;
      
      // Add color based on phase
      if (node.phase !== undefined) {
        const hue = (node.phase * 360).toFixed(0);
        marker.style.color = `hsl(${hue}, 80%, 50%)`;
      }
      
      // Add the marker to the gutter
      this.editor.setGutterMarker(position.line, 'concept-gutter', marker);
    });
  }
}

