// conceptGraphService.js
// Service for building and analyzing concept graphs for semantic conflict detection

/**
 * This service implements the knowledge graph-based approach described in the 
 * "Semantic Commit" paper. It builds a concept graph from intent specifications
 * and provides methods for analyzing semantic impacts using PageRank relevance assessment.
 */
class ConceptGraphService {
  constructor() {
    this.graph = {
      nodes: new Map(), // Map of node id -> node data
      edges: new Map(), // Map of edgeId (source:target:relation) -> edge data
      nodeToEdges: new Map(), // Map of nodeId -> set of edgeIds
    };
    
    // Track document to node mappings for retrieval
    this.documentToNodes = new Map(); // Map of documentId -> Set of nodeIds
    this.nodeToDocuments = new Map(); // Map of nodeId -> Set of documentIds
    
    // Cache for PageRank calculations
    this.pageRankCache = new Map();
    
    // Track when the graph needs to be rebuilt
    this.isDirty = false;
  }
  
  /**
   * Clear the graph completely
   */
  clear() {
    this.graph.nodes.clear();
    this.graph.edges.clear();
    this.graph.nodeToEdges.clear();
    this.documentToNodes.clear();
    this.nodeToDocuments.clear();
    this.pageRankCache.clear();
    this.isDirty = false;
  }
  
  /**
   * Add a document to the graph, extracting concepts and relationships
   * @param {string} documentId - Unique identifier for the document
   * @param {string} content - Document content to analyze
   * @param {Object} metadata - Additional metadata about the document
   * @returns {Promise<void>}
   */
  async addDocument(documentId, content, metadata = {}) {
    try {
      // Extract entities and relationships from content using LLM
      const { entities, relationships } = await this.extractConceptsFromText(content);
      
      // Create a set to track nodes from this document
      if (!this.documentToNodes.has(documentId)) {
        this.documentToNodes.set(documentId, new Set());
      }
      
      // Add entities to the graph
      for (const entity of entities) {
        const nodeId = entity.id || this.normalizeEntityName(entity.name);
        
        // Create or update node
        if (!this.graph.nodes.has(nodeId)) {
          this.graph.nodes.set(nodeId, {
            id: nodeId,
            name: entity.name,
            type: entity.type || 'concept',
            properties: entity.properties || {},
            importance: entity.importance || 1.0
          });
          
          this.nodeToDocuments.set(nodeId, new Set());
          this.graph.nodeToEdges.set(nodeId, new Set());
        }
        
        // Track which document contains this entity
        this.documentToNodes.get(documentId).add(nodeId);
        this.nodeToDocuments.get(nodeId).add(documentId);
      }
      
      // Add relationships to the graph
      for (const rel of relationships) {
        const sourceId = rel.sourceId || this.normalizeEntityName(rel.source);
        const targetId = rel.targetId || this.normalizeEntityName(rel.target);
        const relType = rel.type || 'related_to';
        
        // Skip if either source or target doesn't exist
        if (!this.graph.nodes.has(sourceId) || !this.graph.nodes.has(targetId)) {
          continue;
        }
        
        // Create edge
        const edgeId = `${sourceId}:${targetId}:${relType}`;
        if (!this.graph.edges.has(edgeId)) {
          this.graph.edges.set(edgeId, {
            id: edgeId,
            source: sourceId,
            target: targetId,
            type: relType,
            weight: rel.weight || 1.0,
            properties: rel.properties || {}
          });
          
          // Add to adjacency lists
          this.graph.nodeToEdges.get(sourceId).add(edgeId);
        }
      }
      
      // Mark graph as modified (invalidate caches)
      this.isDirty = true;
    } catch (error) {
      console.error('Error adding document to concept graph:', error);
    }
  }
  
  /**
   * Remove a document from the graph
   * @param {string} documentId - ID of document to remove
   */
  removeDocument(documentId) {
    // Get nodes associated with this document
    const docNodes = this.documentToNodes.get(documentId);
    if (!docNodes) return;
    
    // For each node, remove document association
    for (const nodeId of docNodes) {
      const nodeDocuments = this.nodeToDocuments.get(nodeId);
      nodeDocuments.delete(documentId);
      
      // If node no longer belongs to any document, remove it and its edges
      if (nodeDocuments.size === 0) {
        // Get all edges associated with this node
        const nodeEdges = this.graph.nodeToEdges.get(nodeId);
        
        // Remove all edges
        for (const edgeId of nodeEdges) {
          this.graph.edges.delete(edgeId);
        }
        
        // Remove node itself
        this.graph.nodes.delete(nodeId);
        this.graph.nodeToEdges.delete(nodeId);
        this.nodeToDocuments.delete(nodeId);
      }
    }
    
    // Remove document tracking
    this.documentToNodes.delete(documentId);
    this.isDirty = true;
  }
  
  /**
   * Update a document in the graph
   * @param {string} documentId - ID of document to update
   * @param {string} content - New content
   * @param {Object} metadata - New metadata
   */
  async updateDocument(documentId, content, metadata = {}) {
    this.removeDocument(documentId);
    await this.addDocument(documentId, content, metadata);
  }
  
  /**
   * Extract concepts and relationships from text using LLM
   * This would use an actual LLM in production
   * @param {string} text - Text to analyze
   * @returns {Promise<Object>} - Extracted entities and relationships
   */
  async extractConceptsFromText(text) {
    // In a real implementation, this would call an LLM
    // For this demo, we'll use a simple regex approach to identify potential entities
    
    // Simple entity extraction - look for capitalized terms as potential concepts
    const entities = [];
    const entityMap = new Map();
    
    // Find potential entities (capitalized words, technical terms, etc.)
    const potentialEntities = text.match(/\b([A-Z][a-z]+(\s[A-Z][a-z]+)*|[a-z]+\.[a-z]+|[a-z]+\(\))\b/g) || [];
    
    // Deduplicate and create entity objects
    for (const name of potentialEntities) {
      const normalizedName = this.normalizeEntityName(name);
      if (!entityMap.has(normalizedName)) {
        const entity = {
          id: normalizedName,
          name: name,
          type: name.includes('()') ? 'function' : 
                name.includes('.') ? 'property' : 'concept',
          importance: 1.0
        };
        
        entities.push(entity);
        entityMap.set(normalizedName, entity);
      }
    }
    
    // Generate simple relationships based on co-occurrence in sentences
    const relationships = [];
    const sentences = text.split(/[.!?]+/).filter(s => s.trim().length > 0);
    
    for (const sentence of sentences) {
      const entitiesInSentence = [];
      
      // Find entities in this sentence
      for (const entity of entities) {
        if (sentence.includes(entity.name)) {
          entitiesInSentence.push(entity);
        }
      }
      
      // Create relationships between entities in the same sentence
      for (let i = 0; i < entitiesInSentence.length; i++) {
        for (let j = i + 1; j < entitiesInSentence.length; j++) {
          relationships.push({
            source: entitiesInSentence[i].name,
            sourceId: entitiesInSentence[i].id,
            target: entitiesInSentence[j].name,
            targetId: entitiesInSentence[j].id,
            type: 'co_occurs_with',
            weight: 1.0
          });
        }
      }
    }
    
    return { entities, relationships };
  }
  
  /**
   * Normalize an entity name for use as an ID
   * @param {string} name - Entity name
   * @returns {string} - Normalized ID
   */
  normalizeEntityName(name) {
    return name.toLowerCase().replace(/[\s()\.]/g, '_');
  }
  
  /**
   * Find semantically related documents for a new piece of content
   * Uses PageRank to rank results based on graph connectivity
   * @param {string} content - The new content to find relations for
   * @param {Object} options - Options for retrieval
   * @returns {Promise<Array>} - Ranked list of related document IDs
   */
  async findRelatedDocuments(content, options = {}) {
    const { 
      maxResults = 10, 
      threshold = 0.1,
      alpha = 0.85, // damping factor for PageRank
      iterations = 20 // PageRank iterations
    } = options;
    
    try {
      // Extract entities and relationships from query content
      const { entities } = await this.extractConceptsFromText(content);
      
      if (entities.length === 0) {
        console.warn('No entities found in query content');
        return [];
      }
      
      // Get seed nodes (start nodes for PageRank)
      const seedNodeIds = entities
        .map(e => e.id || this.normalizeEntityName(e.name))
        .filter(id => this.graph.nodes.has(id));
      
      if (seedNodeIds.length === 0) {
        console.warn('No matching entities found in graph');
        return [];
      }
      
      // Run Personalized PageRank from seed nodes
      const nodeScores = this.runPageRank(seedNodeIds, alpha, iterations);
      
      // Get all documents connected to scored nodes, with their aggregated scores
      const documentScores = new Map();
      
      for (const [nodeId, score] of nodeScores.entries()) {
        // Skip nodes with scores below threshold
        if (score < threshold) continue;
        
        // Find documents containing this node
        const documents = this.nodeToDocuments.get(nodeId);
        if (!documents) continue;
        
        // Add score to each document
        for (const docId of documents) {
          const currentScore = documentScores.get(docId) || 0;
          documentScores.set(docId, currentScore + score);
        }
      }
      
      // Convert to array and sort by score
      const rankedDocuments = Array.from(documentScores.entries())
        .map(([docId, score]) => ({ id: docId, score }))
        .sort((a, b) => b.score - a.score)
        .slice(0, maxResults);
      
      return rankedDocuments;
    } catch (error) {
      console.error('Error finding related documents:', error);
      return [];
    }
  }
  
  /**
   * Run Personalized PageRank algorithm on the graph
   * @param {Array<string>} seedNodes - Starting nodes for PageRank
   * @param {number} alpha - Damping factor (default: 0.85)
   * @param {number} iterations - Number of iterations
   * @returns {Map<string, number>} - Map of node IDs to PageRank scores
   */
  runPageRank(seedNodes, alpha = 0.85, iterations = 20) {
    // If no seed nodes provided, return empty result
    if (!seedNodes || seedNodes.length === 0) {
      return new Map();
    }
    
    // Initialize scores
    const scores = new Map();
    for (const nodeId of this.graph.nodes.keys()) {
      scores.set(nodeId, 0);
    }
    
    // Set initial scores for seed nodes
    for (const nodeId of seedNodes) {
      scores.set(nodeId, 1.0 / seedNodes.length);
    }
    
    // Build teleport vector (where random jumps go to)
    const teleport = new Map();
    for (const nodeId of this.graph.nodes.keys()) {
      teleport.set(nodeId, seedNodes.includes(nodeId) ? 1.0 / seedNodes.length : 0);
    }
    
    // Run PageRank iterations
    for (let iter = 0; iter < iterations; iter++) {
      const newScores = new Map();
      
      // Initialize all nodes with teleport component
      for (const nodeId of this.graph.nodes.keys()) {
        newScores.set(nodeId, (1 - alpha) * teleport.get(nodeId));
      }
      
      // For each node, distribute its score to outgoing neighbors
      for (const [nodeId, nodeScore] of scores.entries()) {
        const outEdges = this.graph.nodeToEdges.get(nodeId);
        if (!outEdges || outEdges.size === 0) continue;
        
        // Get total weight of outgoing edges
        let totalWeight = 0;
        const neighbors = [];
        
        for (const edgeId of outEdges) {
          const edge = this.graph.edges.get(edgeId);
          if (!edge) continue;
          
          totalWeight += edge.weight;
          neighbors.push({
            nodeId: edge.target,
            weight: edge.weight
          });
        }
        
        // Distribute score to neighbors proportionally to edge weights
        if (totalWeight > 0) {
          for (const neighbor of neighbors) {
            const currentScore = newScores.get(neighbor.nodeId) || 0;
            const addedScore = alpha * nodeScore * (neighbor.weight / totalWeight);
            newScores.set(neighbor.nodeId, currentScore + addedScore);
          }
        }
      }
      
      // Update scores for next iteration
      for (const [nodeId, score] of newScores.entries()) {
        scores.set(nodeId, score);
      }
    }
    
    return scores;
  }
  
  /**
   * Get statistics about the current graph
   */
  getGraphStats() {
    return {
      nodeCount: this.graph.nodes.size,
      edgeCount: this.graph.edges.size,
      documentCount: this.documentToNodes.size
    };
  }
  
  /**
   * Visualize the graph structure for debugging
   * Returns a simple representation of the graph
   */
  getGraphVisualization() {
    const nodes = Array.from(this.graph.nodes.values()).map(node => ({
      id: node.id,
      name: node.name,
      type: node.type,
      documentCount: this.nodeToDocuments.get(node.id)?.size || 0
    }));
    
    const edges = Array.from(this.graph.edges.values()).map(edge => ({
      source: edge.source,
      sourceName: this.graph.nodes.get(edge.source)?.name || edge.source,
      target: edge.target,
      targetName: this.graph.nodes.get(edge.target)?.name || edge.target,
      type: edge.type,
      weight: edge.weight
    }));
    
    return { nodes, edges };
  }
}

// Create and export singleton instance
const conceptGraphService = new ConceptGraphService();
export default conceptGraphService;

/**
 * Exporter Service
 * 
 * Provides functionality to export concept graphs back to code with lineage comments,
 * and export field state snapshots for branches and PRs.
 * 
 * Part of Phase 3 - Sprint 5 ALAN IDE implementation.
 */

import conceptGraphService from './conceptGraphService';
import { VaultService } from './vaultService';

class ExporterService {
  constructor() {
    this.vaultService = new VaultService();
    
    // Language templates for code generation
    this.languageTemplates = {
      python: {
        functionTemplate: 'def ${name}(${params}):\n${body}\n',
        classTemplate: 'class ${name}${inheritance}:\n${body}\n',
        methodTemplate: '    def ${name}(self, ${params}):\n${body}\n',
        importTemplate: 'import ${module}\n',
        lineageCommentTemplate: '# ALAN-LINEAGE: ${conceptId} | ${timestamp} | ${author}'
      },
      javascript: {
        functionTemplate: 'function ${name}(${params}) {\n${body}\n}\n',
        classTemplate: 'class ${name}${inheritance} {\n${body}\n}\n',
        methodTemplate: '  ${name}(${params}) {\n${body}\n  }\n',
        importTemplate: 'import ${what} from "${module}";\n', 
        lineageCommentTemplate: '// ALAN-LINEAGE: ${conceptId} | ${timestamp} | ${author}'
      },
      typescript: {
        functionTemplate: 'function ${name}(${params}): ${returnType} {\n${body}\n}\n',
        classTemplate: 'class ${name}${inheritance} {\n${body}\n}\n',
        methodTemplate: '  ${name}(${params}): ${returnType} {\n${body}\n  }\n',
        importTemplate: 'import ${what} from "${module}";\n', 
        lineageCommentTemplate: '// ALAN-LINEAGE: ${conceptId} | ${timestamp} | ${author}'
      }
    };
  }
  
  /**
   * Generate code from a concept graph.
   * 
   * @param {Object} options - Export options
   * @param {string} options.graphId - ID of the concept graph to export
   * @param {string} options.language - Target language ('python', 'javascript', 'typescript')
   * @param {boolean} options.includeLineage - Whether to include lineage comments
   * @param {string} options.author - Author name for lineage comments
   * @returns {Promise<Object>} - Promise resolving to the exported code files
   */
  async exportGraphToCode(options = {}) {
    const {
      graphId,
      language = 'python',
      includeLineage = true,
      author = 'ALAN-IDE'
    } = options;
    
    // Get the concept graph
    const graph = conceptGraphService.getConceptGraph(graphId);
    if (!graph) {
      throw new Error(`Concept graph with ID ${graphId} not found`);
    }
    
    // Get the language template
    const template = this.languageTemplates[language];
    if (!template) {
      throw new Error(`Unsupported language: ${language}`);
    }
    
    // Create a result object to hold the generated files
    const result = {
      files: {},
      metadata: {
        graphId,
        language,
        timestamp: Date.now(),
        author
      }
    };
    
    // Sort nodes to ensure proper ordering (imports, then classes, then functions)
    const sortedNodes = this.sortNodesForCodeGeneration(graph.nodes);
    
    // Group nodes by file
    const fileNodes = this.groupNodesByFile(sortedNodes);
    
    // Generate code for each file
    for (const [filename, nodes] of Object.entries(fileNodes)) {
      const code = this.generateFileCode(nodes, {
        language,
        template,
        includeLineage,
        author
      });
      
      result.files[filename] = code;
    }
    
    return result;
  }
  
  /**
   * Sort nodes for code generation to ensure proper ordering.
   * 
   * @param {Array} nodes - Concept graph nodes
   * @returns {Array} - Sorted nodes
   */
  sortNodesForCodeGeneration(nodes) {
    // Clone nodes to avoid modifying the original array
    const sortedNodes = [...nodes];
    
    // Sort by type priority: imports, classes, functions, variables
    const typePriority = {
      import: 0,
      class: 1,
      function: 2,
      method: 3,
      variable: 4
    };
    
    return sortedNodes.sort((a, b) => {
      // First sort by file
      if (a.file !== b.file) {
        return a.file < b.file ? -1 : 1;
      }
      
      // Then sort by type priority
      const aPriority = typePriority[a.type] ?? 999;
      const bPriority = typePriority[b.type] ?? 999;
      if (aPriority !== bPriority) {
        return aPriority - bPriority;
      }
      
      // Then sort by line number if available
      if (a.lineNumber !== undefined && b.lineNumber !== undefined) {
        return a.lineNumber - b.lineNumber;
      }
      
      // Finally sort by name
      return a.label < b.label ? -1 : 1;
    });
  }
  
  /**
   * Group nodes by file.
   * 
   * @param {Array} nodes - Sorted concept graph nodes
   * @returns {Object} - Nodes grouped by filename
   */
  groupNodesByFile(nodes) {
    const fileNodes = {};
    
    for (const node of nodes) {
      const filename = node.file || 'index.js'; // Default filename
      
      if (!fileNodes[filename]) {
        fileNodes[filename] = [];
      }
      
      fileNodes[filename].push(node);
    }
    
    return fileNodes;
  }
  
  /**
   * Generate code for a file from its nodes.
   * 
   * @param {Array} nodes - Nodes for the file
   * @param {Object} options - Code generation options
   * @returns {string} - Generated code
   */
  generateFileCode(nodes, options) {
    const { language, template, includeLineage, author } = options;
    
    let code = '';
    let importsSection = '';
    let classesSection = '';
    let functionsSection = '';
    let variablesSection = '';
    
    // Process nodes by type
    for (const node of nodes) {
      let nodeCode = '';
      const timestamp = new Date().toISOString();
      const lineageComment = includeLineage 
        ? template.lineageCommentTemplate
            .replace('${conceptId}', node.id)
            .replace('${timestamp}', timestamp)
            .replace('${author}', author)
        : '';
      
      switch (node.type) {
        case 'import':
          nodeCode = this.generateImportCode(node, template);
          if (lineageComment) {
            nodeCode = `${lineageComment}\n${nodeCode}`;
          }
          importsSection += nodeCode;
          break;
          
        case 'class':
          nodeCode = this.generateClassCode(node, template, lineageComment, nodes);
          classesSection += nodeCode;
          break;
          
        case 'function':
          nodeCode = this.generateFunctionCode(node, template, lineageComment);
          functionsSection += nodeCode;
          break;
          
        case 'variable':
          nodeCode = this.generateVariableCode(node, language, lineageComment);
          variablesSection += nodeCode;
          break;
      }
    }
    
    // Combine sections with appropriate spacing
    code = importsSection;
    
    if (importsSection && (classesSection || functionsSection || variablesSection)) {
      code += '\n\n';
    }
    
    code += classesSection;
    
    if (classesSection && (functionsSection || variablesSection)) {
      code += '\n';
    }
    
    code += functionsSection;
    
    if (functionsSection && variablesSection) {
      code += '\n';
    }
    
    code += variablesSection;
    
    return code;
  }
  
  /**
   * Generate code for an import node.
   * 
   * @param {Object} node - Import node
   * @param {Object} template - Language template
   * @returns {string} - Generated import code
   */
  generateImportCode(node, template) {
    return template.importTemplate
      .replace('${module}', node.module || node.label)
      .replace('${what}', node.what || '*');
  }
  
  /**
   * Generate code for a class node.
   * 
   * @param {Object} node - Class node
   * @param {Object} template - Language template
   * @param {string} lineageComment - Lineage comment
   * @param {Array} allNodes - All nodes in the file
   * @returns {string} - Generated class code
   */
  generateClassCode(node, template, lineageComment, allNodes) {
    const inheritance = node.inheritance ? ` extends ${node.inheritance}` : '';
    
    // Find methods belonging to this class
    const methods = allNodes.filter(n => 
      n.type === 'method' && n.classId === node.id
    );
    
    // Generate method code
    let methodsCode = '';
    for (const method of methods) {
      methodsCode += this.generateMethodCode(method, template, lineageComment);
    }
    
    // Generate class body
    const body = methodsCode || '    pass' + (template.language === 'python' ? '' : '\n');
    
    // Generate class code
    let classCode = template.classTemplate
      .replace('${name}', node.label)
      .replace('${inheritance}', inheritance)
      .replace('${body}', body);
    
    // Add lineage comment
    if (lineageComment) {
      classCode = `${lineageComment}\n${classCode}`;
    }
    
    return classCode;
  }
  
  /**
   * Generate code for a method node.
   * 
   * @param {Object} node - Method node
   * @param {Object} template - Language template
   * @param {string} lineageComment - Lineage comment
   * @returns {string} - Generated method code
   */
  generateMethodCode(node, template, lineageComment) {
    const params = node.parameters || '';
    const returnType = node.returnType ? `: ${node.returnType}` : '';
    const body = node.body 
      ? node.body.split('\n').map(line => '        ' + line).join('\n')
      : '        pass';
    
    let methodCode = template.methodTemplate
      .replace('${name}', node.label)
      .replace('${params}', params)
      .replace('${returnType}', returnType)
      .replace('${body}', body);
    
    // Add lineage comment
    if (lineageComment) {
      const indentedComment = '    ' + lineageComment;
      methodCode = `${indentedComment}\n${methodCode}`;
    }
    
    return methodCode;
  }
  
  /**
   * Generate code for a function node.
   * 
   * @param {Object} node - Function node
   * @param {Object} template - Language template
   * @param {string} lineageComment - Lineage comment
   * @returns {string} - Generated function code
   */
  generateFunctionCode(node, template, lineageComment) {
    const params = node.parameters || '';
    const returnType = node.returnType ? `: ${node.returnType}` : '';
    const body = node.body 
      ? node.body.split('\n').map(line => '    ' + line).join('\n')
      : '    pass';
    
    let functionCode = template.functionTemplate
      .replace('${name}', node.label)
      .replace('${params}', params)
      .replace('${returnType}', returnType)
      .replace('${body}', body);
    
    // Add lineage comment
    if (lineageComment) {
      functionCode = `${lineageComment}\n${functionCode}`;
    }
    
    return functionCode;
  }
  
  /**
   * Generate code for a variable node.
   * 
   * @param {Object} node - Variable node
   * @param {string} language - Target language
   * @param {string} lineageComment - Lineage comment
   * @returns {string} - Generated variable code
   */
  generateVariableCode(node, language, lineageComment) {
    const value = node.value !== undefined ? node.value : 'None';
    const type = node.type ? `: ${node.type}` : '';
    
    let varCode;
    if (language === 'python') {
      varCode = `${node.label}${type} = ${value}\n`;
    } else {
      const keyword = node.constant ? 'const' : 'let';
      varCode = `${keyword} ${node.label}${type} = ${value};\n`;
    }
    
    // Add lineage comment
    if (lineageComment) {
      varCode = `${lineageComment}\n${varCode}`;
    }
    
    return varCode;
  }
  
  /**
   * Export a field state snapshot for branch/PR.
