/**
 * Accessibility Test Utilities
 * 
 * Provides utilities for testing component accessibility using axe-core.
 * Part of the ALAN IDE Phase 3 accessibility testing strategy.
 */

import { render } from '@testing-library/react';
import { axe, toHaveNoViolations } from 'jest-axe';

// Add custom jest matchers for accessibility
expect.extend(toHaveNoViolations);

/**
 * Configuration for axe-core tests
 * These rules align with WCAG 2.1 AA compliance requirements
 */
const axeConfig = {
  rules: {
    // Ensure color contrast meets WCAG 2.1 AA (4.5:1 for normal text, 3:1 for large text)
    'color-contrast': { enabled: true },

    // Ensure all images have alt text
    'image-alt': { enabled: true },

    // Ensure proper heading structure
    'heading-order': { enabled: true },

    // Ensure form elements have labels
    'label': { enabled: true },
    'label-content-name-mismatch': { enabled: true },

    // Ensure ARIA attributes are used correctly
    'aria-roles': { enabled: true },
    'aria-valid-attr': { enabled: true },
    'aria-valid-attr-value': { enabled: true },

    // Ensure keyboard navigation works properly
    'tabindex': { enabled: true },

    // Ensure proper focus management
    'focus-order-semantics': { enabled: true }
  }
};

/**
 * Test a component for accessibility violations
 * 
 * @param {React.ReactElement} ui - The component to test
 * @param {Object} options - Additional options for render
 * @returns {Promise<axe.AxeResults>} - axe-core results
 */
export async function checkAccessibility(ui, options = {}) {
  const container = render(ui, options).container;
  return await axe(container, axeConfig);
}

/**
 * Perform a full keyboard navigation test on a component
 * 
 * @param {HTMLElement} container - The container element to test
 * @param {Array<string>} expectedTabOrder - Optional array of test IDs in expected tab order
 * @returns {Array<HTMLElement>} - Array of elements in tab order
 */
export function testKeyboardNavigation(container, expectedTabOrder = []) {
  // Get all focusable elements
  const focusableElements = getFocusableElements(container);

  // If expected order is provided, verify it
  if (expectedTabOrder.length > 0) {
    const actualOrder = focusableElements
      .filter(el => el.hasAttribute('data-testid'))
      .map(el => el.getAttribute('data-testid'));

    // Check if actual tab order matches expected
    expect(actualOrder).toEqual(expectedTabOrder);
  }

  return focusableElements;
}

/**
 * Get all focusable elements within a container
 * 
 * @param {HTMLElement} container - The container to search within
 * @returns {Array<HTMLElement>} - Array of focusable elements
 */
function getFocusableElements(container) {
  // Query for all potentially focusable elements
  const elements = container.querySelectorAll(
    'a[href], button, input, textarea, select, details, [tabindex]:not([tabindex="-1"])'
  );

  // Filter out disabled and hidden elements
  return Array.from(elements).filter(el => {
    // Skip disabled elements
    if (el.hasAttribute('disabled')) return false;

    // Skip hidden elements
    if (el.style.display === 'none' || el.style.visibility === 'hidden') return false;

    // Skip elements with hidden ancestors
    let parent = el.parentElement;
    while (parent !== container) {
      if (
        parent.style.display === 'none' || 
        parent.style.visibility === 'hidden' ||
        parent.hasAttribute('hidden')
      ) {
        return false;
      }
      parent = parent.parentElement;
    }

    return true;
  }).sort((a, b) => {
    // Sort by tabindex
    const aIndex = a.getAttribute('tabindex');
    const bIndex = b.getAttribute('tabindex');

    if (aIndex !== null && bIndex !== null) {
      return parseInt(aIndex) - parseInt(bIndex);
    }

    if (aIndex !== null) return parseInt(aIndex) < 0 ? 1 : -1;
    if (bIndex !== null) return parseInt(bIndex) < 0 ? -1 : 1;

    // If no tabindex, use DOM order
    return 0;
  });
}

/**
 * Test ARIA attributes for a component
 * 
 * @param {HTMLElement} container - The container to test
 * @param {Object} expectedRoles - Map of test IDs to expected ARIA roles
 */
export function testARIAAttributes(container, expectedRoles = {}) {
  for (const [testId, expectedRole] of Object.entries(expectedRoles)) {
    const element = container.querySelector(`[data-testid="${testId}"]`);
    expect(element).not.toBeNull();
    expect(element).toHaveAttribute('role', expectedRole);
  }
}

/**
 * Verify a component maintains proper focus management
 * 
 * @param {HTMLElement} container - The container element to test
 * @param {Function} triggerAction - Function to trigger an action that should manage focus
 * @param {string} expectedFocusTestId - The test ID of the element that should receive focus
 */
export function testFocusManagement(container, triggerAction, expectedFocusTestId) {
  // Track focus changes
  const focusChanges = [];
  const originalAddEventListener = document.addEventListener;

  document.addEventListener = (event, callback, options) => {
    if (event === 'focus') {
      const originalCallback = callback;
      const wrappedCallback = (e) => {
        focusChanges.push(e.target);
        return originalCallback(e);
      };
      return originalAddEventListener.call(document, event, wrappedCallback, options);
    }
    return originalAddEventListener.call(document, event, callback, options);
  };

  // Trigger the action
  triggerAction();

  // Restore original addEventListener
  document.addEventListener = originalAddEventListener;

  // Check if focus was set correctly
  const expectedElement = container.querySelector(`[data-testid="${expectedFocusTestId}"]`);
  expect(expectedElement).not.toBeNull();

  // Verify focus was set to the expected element
  expect(document.activeElement).toBe(expectedElement);
}

