        }

        refactorService.applyTransformation(transformType, nodeId, options)
          .then(() => {
            resolve({ success: true, operationId: i });
          })
          .catch(error => {
            resolve({ success: false, operationId: i, error });
          });
      }, Math.random() * 500); // Staggered start within 500ms
    });

