 * Integration Test for ExecutionTracer + FieldMeditationMode
 * 
 * Tests the interaction between the ExecutionTracerService and FieldMeditationMode
 * components, ensuring execution events are properly visualized.
 */

import React from 'react';
import { render, screen, waitFor, act } from '@testing-library/react';
// No render required for this test
import FieldMeditationMode from '../../../components/FieldMeditationMode/FieldMeditationMode';
import executionTracerService from '../../../services/executionTracerService';
import conceptGraphService from '../../../services/conceptGraphService';

// Mock WebSocket
class MockWebSocket {
  constructor(url) {
    this.url = url;
    this.onopen = null;
    this.onmessage = null;
    this.onclose = null;
    this.onerror = null;
    this.readyState = 0; // CONNECTING
    
    // Simulate connecting after construction
    setTimeout(() => {
      this.readyState = 1; // OPEN
      if (this.onopen) {
        this.onopen();
      }
    }, 50);
  }
  
  send(data) {
    const parsedData = JSON.parse(data);
    
    // Simulate response for execute_python
    if (parsedData.type === 'execute_python') {
      setTimeout(() => {
        this.simulateExecutionStart();
        
        // Simulate a few execution events
        setTimeout(() => this.simulateExecutionEvent('node_1', 0.2), 100);
        setTimeout(() => this.simulateExecutionEvent('node_2', 0.5), 200);
        setTimeout(() => this.simulateExecutionEvent('node_3', 0.8), 300);
        
        // Simulate execution end
        setTimeout(() => this.simulateExecutionEnd(), 400);
      }, 50);
    }
  }
  
  simulateMessage(data) {
    if (this.onmessage) {
      this.onmessage({ data: JSON.stringify(data) });
    }
  }
  
  simulateExecutionStart() {
    this.simulateMessage({
      type: 'execution_start',
      payload: {
        timestamp: Date.now()
      }
    });
  }
  
  simulateExecutionEvent(nodeId, phase) {
    this.simulateMessage({
      type: 'execution_event',
      payload: {
        node_id: nodeId,
        phase,
        timestamp: Date.now(),
        context: {
          type: 'function_entry',
          name: `function_${nodeId}`,
          line: Math.floor(Math.random() * 100)
        }
      }
    });
  }
  
  simulateExecutionEnd() {
    this.simulateMessage({
      type: 'execution_end',
      payload: {
        timestamp: Date.now()
      }
    });
  }
  
  close() {
    this.readyState = 3; // CLOSED
    if (this.onclose) {
      this.onclose();
    }
  }
}

// Mock concept graph service
jest.mock('../../../services/conceptGraphService', () => ({
  getNode: jest.fn(nodeId => ({
    id: nodeId,
    label: `Node ${nodeId}`,
    type: 'function',
    phase: Math.random()
  })),
  getConceptGraph: jest.fn(() => ({
    nodes: [
      { id: 'node_1', label: 'Node node_1', type: 'function', phase: 0.25 },
      { id: 'node_2', label: 'Node node_2', type: 'function', phase: 0.5 },
      { id: 'node_3', label: 'Node node_3', type: 'function', phase: 0.75 }
    ],
    edges: []
  }))
}));

// Mock sample concept data for visualization
const sampleConceptData = {
  nodes: [
    { id: 'node_1', label: 'Node 1', phase: 0.25 },
    { id: 'node_2', label: 'Node 2', phase: 0.5 },
    { id: 'node_3', label: 'Node 3', phase: 0.75 }
  ],
  edges: [
    { source: 'node_1', target: 'node_2' },
    { source: 'node_2', target: 'node_3' }
  ]
};

describe('ExecutionTracer + FieldMeditationMode Integration', () => {
  let originalWebSocket;
  
  beforeEach(() => {
    // Store original WebSocket
    originalWebSocket = global.WebSocket;
    
    // Mock WebSocket
    global.WebSocket = MockWebSocket;
    
    // Reset executionTracerService
    executionTracerService.isConnected = false;
    executionTracerService.isTracing = false;
    executionTracerService.socket = null;
    executionTracerService.traceBuffer = [];
    executionTracerService.executionState = {
      currentNode: null,
      currentPhase: 0,
      callStack: [],
      variables: {},
      timestamp: 0,
      divergences: []
    };
    executionTracerService.listeners = {
      traceUpdate: [],
      traceComplete: [],
      divergenceDetected: [],
      connectionChange: []
    };
    
    // Spy on notifyListeners
    jest.spyOn(executionTracerService, 'notifyListeners');
  });
  
  afterEach(() => {
    // Restore WebSocket
    global.WebSocket = originalWebSocket;
    
    // Restore spies
    jest.restoreAllMocks();
  });
  
  test('FieldMeditationMode shows connection status', async () => {
    render(
      <FieldMeditationMode
        conceptData={sampleConceptData}
        pythonCode="print('Hello, world!')"
      />
    );
    
    // Initially disconnected
    expect(screen.getByText(/not connected/i)).toBeInTheDocument();
    
    // Connect to tracer
    await act(async () => {
      await executionTracerService.connect();
    });
    
    // Connection status should be removed once connected
    await waitFor(() => {
      expect(screen.queryByText(/not connected/i)).not.toBeInTheDocument();
    });
  });
  
  test('Python execution events update the visualization', async () => {
    const onStateChange = jest.fn();
    
    render(
      <FieldMeditationMode
        conceptData={sampleConceptData}
        pythonCode="print('Hello, world!')"
        onStateChange={onStateChange}
      />
    );
    
    // Connect to tracer
    await act(async () => {
      await executionTracerService.connect();
    });
    
    // Execute code
    await act(async () => {
      screen.getByText(/execute code/i).click();
    });
    
    // Wait for execution events to be processed
    await waitFor(() => {
      expect(executionTracerService.notifyListeners).toHaveBeenCalledWith(
        'traceUpdate',
        expect.objectContaining({
          isTracing: true
        })
      );
    });
    
    // Should show "LIVE" badge during execution
    expect(screen.getByText(/live/i)).toBeInTheDocument();
    
    // Wait for execution to complete
    await waitFor(() => {
      expect(executionTracerService.notifyListeners).toHaveBeenCalledWith(
        'traceComplete',
        expect.any(Object)
      );
    }, { timeout: 500 });
    
    // State change should have been called with updated active nodes
    expect(onStateChange).toHaveBeenCalledWith(
      expect.objectContaining({
        activeNodes: expect.arrayContaining(['node_1', 'node_2', 'node_3'])
      })
    );
    
    // Verify that execution produces visualization data
    // Wait for placeholder texts to be removed, indicating visualization data is present
    await waitFor(() => {
      expect(screen.queryByText(/execute code to see koopman spectrum/i)).not.toBeInTheDocument();
    });
    
    await waitFor(() => {
      expect(screen.queryByText(/execute code to see entropy wave/i)).not.toBeInTheDocument();
    });
    
    // Check that visualization components are rendered after execution
    expect(screen.getByText(/koopman spectrum trail/i)).toBeInTheDocument();
    expect(screen.getByText(/spectral entropy wave/i)).toBeInTheDocument();
  });
  
  test('Divergence detection is triggered and displayed', async () => {
    // Mock a divergence by making one node's phase very different
    conceptGraphService.getNode.mockImplementationOnce(nodeId => ({
      id: nodeId,
      label: `Node ${nodeId}`,
      type: 'function',
      phase: 0.8 // Much different from the 0.2 in the event
    }));
    
    const onDivergenceDetected = jest.fn();
    
    render(
      <FieldMeditationMode
        conceptData={sampleConceptData}
        pythonCode="print('Hello, world!')"
        onDivergenceDetected={onDivergenceDetected}
      />
    );
    
    // Connect to tracer
    await act(async () => {
      await executionTracerService.connect();
    });
    
    // Manually trigger divergence detection
    act(() => {
      executionTracerService.executionState.currentNode = 'node_1';
      executionTracerService.executionState.currentPhase = 0.2;
      executionTracerService.detectDivergences();
    });
    
    // Wait for divergence to be detected
    await waitFor(() => {
      expect(onDivergenceDetected).toHaveBeenCalledWith(
        expect.objectContaining({
          type: 'phase_divergence',
          nodeId: 'node_1'
        })
      );
    });
    
    // Execute code to force rendering of divergence
    await act(async () => {
      executionTracerService.handleExecutionStart({});
      executionTracerService.executionState.divergences = [{
        type: 'phase_divergence',
        nodeId: 'node_1',
        nodeName: 'Node node_1',
        severity: 'warning'
      }];
      executionTracerService.emitTraceUpdate();
    });
    
    // Wait for divergence warnings to appear
    await waitFor(() => {
      expect(screen.getByText(/divergence warnings/i)).toBeInTheDocument();
    });
    
    // Then check if the specific divergence type is shown
    expect(screen.getByText(/phase_divergence/i)).toBeInTheDocument();
  });
});

