/**
 * Tests for PersonaSelector
 * 
 * Tests the functionality of the PersonaSelector component.
 */

import React from 'react';
import { screen, fireEvent, within } from '@testing-library/react';
import { renderWithProviders } from '../../test-utils';
import PersonaSelector from '../../../components/PersonaSelector/PersonaSelector';
import { PersonaProvider, PERSONAS } from '../../../components/PersonaSelector/PersonaContext';

describe('PersonaSelector', () => {
  test('renders correctly with default persona', () => {
    renderWithProviders(<PersonaSelector />);
    
    // Check if the current persona is displayed
    expect(screen.getByText('Power Coder')).toBeInTheDocument();
  });
  
  test('opens menu when clicked', () => {
    renderWithProviders(<PersonaSelector />);
    
    // Initially, menu should be closed
    expect(screen.queryByRole('menu')).not.toBeInTheDocument();
    
    // Click the button to open the menu
    fireEvent.click(screen.getByRole('button', { name: /select persona/i }));
    
    // Menu should now be open
    const menu = screen.getByRole('menu');
    expect(menu).toBeInTheDocument();
    
    // All personas should be in the menu
    Object.values(PERSONAS).forEach(persona => {
      expect(within(menu).getByText(persona.name)).toBeInTheDocument();
      expect(within(menu).getByText(persona.description)).toBeInTheDocument();
    });
  });
  
  test('closes menu when a persona is selected', () => {
    renderWithProviders(<PersonaSelector />);
    
    // Open the menu
    fireEvent.click(screen.getByRole('button', { name: /select persona/i }));
    expect(screen.getByRole('menu')).toBeInTheDocument();
    
    // Select a different persona
    fireEvent.click(screen.getByRole('menuitem', { name: /switch to concept architect persona/i }));
    
    // Menu should be closed
    expect(screen.queryByRole('menu')).not.toBeInTheDocument();
    
    // Selected persona should be displayed
    expect(screen.getByText('Concept Architect')).toBeInTheDocument();
  });
  
  test('renders with different position props', () => {
    const { rerender } = renderWithProviders(<PersonaSelector position="top-left" />);
    
    // Check if the component has the correct position class
    const selector = screen.getByTestId('persona-selector-container');
    expect(selector).toHaveClass('top-left');
    
    // Rerender with a different position
    rerender(<PersonaProvider><PersonaSelector position="bottom-right" /></PersonaProvider>);
    expect(selector).toHaveClass('bottom-right');
  });
  
  test('applies custom className', () => {
    renderWithProviders(<PersonaSelector className="custom-class" />);
    
    const selector = screen.getByTestId('persona-selector-container');
    expect(selector).toHaveClass('custom-class');
  });
  
  test('shows keyboard shortcut hint', () => {
    renderWithProviders(<PersonaSelector />);
    
    // Open the menu
    fireEvent.click(screen.getByRole('button', { name: /select persona/i }));
    
    // Check if the keyboard shortcut hint is shown
    expect(screen.getByText(/press/i)).toBeInTheDocument();
    expect(screen.getByText(/alt/i)).toBeInTheDocument();
    expect(screen.getByText(/p/i)).toBeInTheDocument();
    expect(screen.getByText(/panic switch/i)).toBeInTheDocument();
  });
  
  test('has correct accessibility attributes', () => {
    renderWithProviders(<PersonaSelector />);
    
    // Check button accessibility
    const button = screen.getByRole('button', { name: /select persona/i });
    expect(button).toHaveAttribute('aria-expanded', 'false');
    expect(button).toHaveAttribute('aria-haspopup', 'true');
    
    // Open menu and check menu accessibility
    fireEvent.click(button);
    expect(button).toHaveAttribute('aria-expanded', 'true');
    
    const menu = screen.getByRole('menu');
    expect(menu).toHaveAttribute('aria-orientation', 'vertical');
    
    // Check menu items
    const menuItems = screen.getAllByRole('menuitem');
    expect(menuItems.length).toBe(Object.keys(PERSONAS).length);
    
    // Each menu item should have an aria-label
    menuItems.forEach(item => {
      expect(item).toHaveAttribute('aria-label');
    });
  });
});

/**
 * Test Utilities for ALAN IDE Phase 3
 * 
 * Common utilities for standardizing test setup and rendering components with
 * all required context providers.
 */

import React from 'react';
import { render } from '@testing-library/react';
import { PersonaProvider } from '../components/PersonaSelector/PersonaContext';

/**
 * Custom render function that wraps components with necessary providers
 * 
 * @param {React.ReactElement} ui - The React element to render
 * @param {Object} options - Additional rendering options
 * @param {Object} providerProps - Props to pass to the PersonaProvider
 * @returns {Object} The rendered component with additional testing utilities
 */
export function renderWithProviders(
  ui,
  {
    ...renderOptions
  } = {}
) {
  function Wrapper({ children }) {
    return (
      <PersonaProvider>
        {children}
      </PersonaProvider>
    );
  }
  
  return render(ui, { wrapper: Wrapper, ...renderOptions });
}

/**
 * Utility function to wait for a component to stabilize
 * after state changes or animations
 * 
 * @param {number} ms - Number of milliseconds to wait
 * @returns {Promise} A promise that resolves after the specified time
 */
export const waitForStability = (ms = 50) => new Promise(
  resolve => setTimeout(resolve, ms)
);

/**
 * Mock implementation of window.matchMedia for components that use media queries
 */
export const setupMatchMedia = () => {
  Object.defineProperty(window, 'matchMedia', {
    writable: true,
    value: jest.fn().mockImplementation(query => ({
      matches: false,
      media: query,
      onchange: null,
      addListener: jest.fn(),
      removeListener: jest.fn(),
      addEventListener: jest.fn(),
      removeEventListener: jest.fn(),
      dispatchEvent: jest.fn(),
    })),
  });
};

