2 ⃣ Front-end drop-in (if you’d rather just paste fresh code)

jsx

Copy code/* src/components/UploadButton.jsx */
import { useRef } from 'react';

export default function UploadButton({ onFiles }) {
  const ref = useRef();

  const pick = () =&gt; ref.current?.click();

  const handle = (e) =&gt; {
    const files = Array.from(e.target.files);
    onFiles(files);          // bubble up
    e.target.value = '';     // reset so same file can be re-picked
  };

  return (
    &lt;&gt;
      &lt;button
        type="button"
        title="Attach PDF(s)"
        onClick={pick}
        className="text-xl hover:scale-110 transition"
      &gt;📎&lt;/button&gt;

      &lt;input
        ref={ref}
        type="file"
        accept="application/pdf"
        multiple
        onChange={handle}
        className="hidden"   /* hidden input */
      /&gt;
    &lt;/&gt;
  );
}

