<content>#!/usr/bin/env node
import { Server } from '@modelcontextprotocol/sdk/server/index.js';
import { StdioServerTransport } from '@modelcontextprotocol/sdk/server/stdio.js';
import {
  CallToolRequestSchema,
  ErrorCode,
  ListResourcesRequestSchema,
  ReadResourceRequestSchema,
  ListToolsRequestSchema,
  McpError,
} from '@modelcontextprotocol/sdk/types.js';
import puppeteer from 'puppeteer';

class PuppeteerServer {
  constructor() {
    this.server = new Server(
      {
        name: 'puppeteer-server',
        version: '1.0.0',
      },
      {
        capabilities: {
          resources: {},
          tools: {},
        },
      }
    );

    // Initialize browser and page as null
    this.browser = null;
    this.page = null;
    this.screenshots = new Map();

    // Setup server handlers
    this.setupResourceHandlers();
    this.setupToolHandlers();

    // Error handling
    this.server.onerror = (error) => console.error('[MCP Error]', error);
    process.on('SIGINT', async () => {
      await this.cleanupBrowser();
      await this.server.close();
      process.exit(0);
    });
  }

  async cleanupBrowser() {
    if (this.browser) {
      try {
        await this.browser.close();
      } catch (error) {
        console.error('Error closing browser:', error);
      }
      this.browser = null;
      this.page = null;
    }
  }

  async ensureBrowser(launchOptions = {}, allowDangerous = false) {
    const defaultOptions = {
      headless: false,
      defaultViewport: { width: 800, height: 600 },
    };

    const options = { ...defaultOptions, ...launchOptions };

    // Check for dangerous options if not allowed
    if (!allowDangerous) {
      const dangerousArgs = ['--no-sandbox', '--disable-web-security'];
      if (options.args) {
        for (const arg of dangerousArgs) {
          if (options.args.includes(arg)) {
            throw new McpError(
              ErrorCode.InvalidParams,
              `Dangerous launch option detected: ${arg}. Set allowDangerous to true to use this option.`
            );
          }
        }
      }
    }

    // If browser is already open, close it if options changed
    if (this.browser) {
      await this.cleanupBrowser();
    }

    try {
      this.browser = await puppeteer.launch(options);
      this.page = await this.browser.newPage();

      // Capture console logs
      this.consoleLogs = [];
      this.page.on('console', (msg) => {
        const logEntry = `[${new Date().toISOString()}] ${msg.type()}: ${msg.text()}`;
        this.consoleLogs.push(logEntry);
        console.error(logEntry); // Log to stderr for debugging
      });
    } catch (error) {
      throw new McpError(
        ErrorCode.InternalError,
        `Failed to launch browser: ${error.message}`
      );
    }
  }

  setupResourceHandlers() {
    // List available resources
    this.server.setRequestHandler(ListResourcesRequestSchema, async () => {
      const resources = [
        {
          uri: 'console://logs',
          name: 'Browser Console Logs',
          description: 'Console output from the browser',
          mimeType: 'text/plain',
        },
      ];

      // Add all screenshots
      for (const [name] of this.screenshots) {
        resources.push({
          uri: `screenshot://${name}`,
          name: `Screenshot: ${name}`,
          description: `Screenshot captured with name '${name}'`,
          mimeType: 'image/png',
        });
      }

      return { resources };
    });

    // Read resources
    this.server.setRequestHandler(
      ReadResourceRequestSchema,
      async (request) => {
        const { uri } = request.params;

        // Handle console logs
        if (uri === 'console://logs') {
          return {
            contents: [
              {
                uri,
                mimeType: 'text/plain',
                text: this.consoleLogs.join('\n'),
              },
            ],
          };
        }

        // Handle screenshots
        const screenshotMatch = uri.match(/^screenshot:\/\/(.+)$/);
        if (screenshotMatch) {
          const name = screenshotMatch[1];
          const screenshot = this.screenshots.get(name);

          if (!screenshot) {
            throw new McpError(
              ErrorCode.NotFound,
              `Screenshot '${name}' not found`
            );
          }

          return {
            contents: [
              {
                uri,
                mimeType: 'image/png',
                base64: screenshot.toString('base64'),
              },
            ],
          };
        }

        throw new McpError(ErrorCode.NotFound, `Resource not found: ${uri}`);
      }
    );
  }

  setupToolHandlers() {
    // List available tools
    this.server.setRequestHandler(ListToolsRequestSchema, async () => ({
      tools: [
        {
          name: 'puppeteer_navigate',
          description: 'Navigate to a URL in the browser',
          inputSchema: {
            type: 'object',
            properties: {
              url: {
                type: 'string',
                description: 'URL to navigate to',
              },
              launchOptions: {
                type: 'object',
                description: 'Options for launching the browser',
              },
              allowDangerous: {
                type: 'boolean',
                description: 'Allow dangerous launch options',
                default: false,
              },
            },
            required: ['url'],
          },
        },
        {
          name: 'puppeteer_screenshot',
          description: 'Take a screenshot of the page or element',
          inputSchema: {
            type: 'object',
            properties: {
              name: {
                type: 'string',
                description: 'Name for the screenshot',
              },
              selector: {
                type: 'string',
                description: 'CSS selector of element to screenshot (optional)',
              },
              width: {
                type: 'number',
                description: 'Width of the viewport',
                default: 800,
              },
              height: {
                type: 'number',
                description: 'Height of the viewport',
                default: 600,
              },
            },
            required: ['name'],
          },
        },
        {
          name: 'puppeteer_click',
          description: 'Click an element on the page',
          inputSchema: {
            type: 'object',
            properties: {
              selector: {
                type: 'string',
                description: 'CSS selector of element to click',
              },
            },
            required: ['selector'],
          },
        },
        {
          name: 'puppeteer_hover',
          description: 'Hover over an element on the page',
          inputSchema: {
            type: 'object',
            properties: {
              selector: {
                type: 'string',
                description: 'CSS selector of element to hover over',
              },
            },
            required: ['selector'],
          },
        },
        {
          name: 'puppeteer_fill',
          description: 'Fill a form field with text',
          inputSchema: {
            type: 'object',
            properties: {
              selector: {
                type: 'string',
                description: 'CSS selector of input field',
              },
              value: {
                type: 'string',
                description: 'Text to enter in the field',
              },
            },
            required: ['selector', 'value'],
          },
        },
        {
          name: 'puppeteer_select',
          description: 'Select an option from a dropdown',
          inputSchema: {
            type: 'object',
            properties: {
              selector: {
                type: 'string',
                description: 'CSS selector of select element',
              },
              value: {
                type: 'string',
                description: 'Value to select',
              },
            },
            required: ['selector', 'value'],
          },
        },
        {
          name: 'puppeteer_evaluate',
          description: 'Execute JavaScript in the browser',
          inputSchema: {
            type: 'object',
            properties: {
              script: {
                type: 'string',
                description: 'JavaScript code to execute',
              },
            },
            required: ['script'],
          },
        },
      ],
    }));

    // Handle tool calls
    this.server.setRequestHandler(
      CallToolRequestSchema,
      async (request) => {
        const { name, arguments: args } = request.params;

        // Check if browser is initialized for all tools except navigate
        if (name !== 'puppeteer_navigate' && !this.browser) {
          throw new McpError(
            ErrorCode.Failed,
            'Browser not initialized. Call puppeteer_navigate first.'
          );
        }

        try {
          switch (name) {
            case 'puppeteer_navigate':
              return await this.handleNavigate(args);
            case 'puppeteer_screenshot':
              return await this.handleScreenshot(args);
            case 'puppeteer_click':
              return await this.handleClick(args);
            case 'puppeteer_hover':
              return await this.handleHover(args);
            case 'puppeteer_fill':
              return await this.handleFill(args);
            case 'puppeteer_select':
              return await this.handleSelect(args);
            case 'puppeteer_evaluate':
              return await this.handleEvaluate(args);
            default:
              throw new McpError(
                ErrorCode.MethodNotFound,
                `Unknown tool: ${name}`
              );
          }
        } catch (error) {
          if (error instanceof McpError) {
            throw error;
          }
          throw new McpError(
            ErrorCode.InternalError,
            `Error executing ${name}: ${error.message}`
          );
        }
      }
    );
  }

  async handleNavigate(args) {
    const { url, launchOptions, allowDangerous = false } = args;

    // Initialize or reinitialize browser if needed
    await this.ensureBrowser(launchOptions, allowDangerous);

    try {
      await this.page.goto(url, { waitUntil: 'domcontentloaded' });
      const title = await this.page.title();

      return {
        content: [
          {
            type: 'text',
            text: `Navigated to ${url}. Page title: ${title}`,
          },
        ],
      };
    } catch (error) {
      return {
        content: [
          {
            type: 'text',
            text: `Error navigating to ${url}: ${error.message}`,
          },
        ],
        isError: true,
      };
    }
  }

  async handleScreenshot(args) {
    const { name, selector, width = 800, height = 600 } = args;

    try {
      // Set viewport size
      await this.page.setViewport({ width, height });

      let screenshot;
      if (selector) {
        // Take screenshot of specific element
        const element = await this.page.$(selector);
        if (!element) {
          throw new McpError(
            ErrorCode.NotFound,
            `Element not found: ${selector}`
          );
        }
        screenshot = await element.screenshot();
      } else {
        // Take full page screenshot
        screenshot = await this.page.screenshot();
      }

      // Store the screenshot
      this.screenshots.set(name, screenshot);

      return {
        content: [
          {
            type: 'text',
            text: `Screenshot '${name}' taken successfully.`,
          },
        ],
      };
    } catch (error) {
      return {
        content: [
          {
            type: 'text',
            text: `Error taking screenshot: ${error.message}`,
          },
        ],
        isError: true,
      };
    }
  }

  async handleClick(args) {
    const { selector } = args;

    try {
      await this.page.waitForSelector(selector, { timeout: 5000 });
      await this.page.click(selector);
      return {
        content: [
          {
            type: 'text',
            text: `Clicked element: ${selector}`,
          },
        ],
      };
    } catch (error) {
      return {
        content: [
          {
            type: 'text',
            text: `Error clicking element: ${error.message}`,
          },
        ],
        isError: true,
      };
    }
  }

  async handleHover(args) {
    const { selector } = args;

    try {
      await this.page.waitForSelector(selector, { timeout: 5000 });
      await this.page.hover(selector);
      return {
        content: [
          {
            type: 'text',
            text: `Hovered over element: ${selector}`,
          },
        ],
      };
    } catch (error) {
      return {
        content: [
          {
            type: 'text',
            text: `Error hovering over element: ${error.message}`,
          },
        ],
        isError: true,
      };
    }
  }

  async handleFill(args) {
    const { selector, value } = args;

    try {
      await this.page.waitForSelector(selector, { timeout: 5000 });
      await this.page.type(selector, value);
      return {
        content: [
          {
            type: 'text',
            text: `Filled element ${selector} with text`,
          },
        ],
      };
    } catch (error) {
      return {
        content: [
          {
            type: 'text',
            text: `Error filling form field: ${error.message}`,
          },
        ],
        isError: true,
      };
    }
  }

  async handleSelect(args) {
    const { selector, value } = args;

    try {
      await this.page.waitForSelector(selector, { timeout: 5000 });
      await this.page.select(selector, value);
      return {
        content: [
          {
            type: 'text',
            text: `Selected value '${value}' in dropdown ${selector}`,
          },
        ],
      };
    } catch (error) {
      return {
        content: [
          {
            type: 'text',
            text: `Error selecting option: ${error.message}`,
          },
        ],
        isError: true,
      };
    }
  }

  async handleEvaluate(args) {
    const { script } = args;

    try {
      const result = await this.page.evaluate(script);
      return {
        content: [
          {
            type: 'text',
            text: `Script executed successfully. Result: ${JSON.stringify(
              result
            )}`,
          },
        ],
      };
    } catch (error) {
      return {
        content: [
          {
            type: 'text',
            text: `Error executing script: ${error.message}`,
          },
        ],
        isError: true,
      };
    }
  }

  async run() {
    const transport = new StdioServerTransport();
    await this.server.connect(transport);
    console.error('Puppeteer MCP server running on stdio');
  }
}

const server = new PuppeteerServer();
server.run().catch(console.error);
