"""
Manifest parser and validator for ELFIN packages.

This module provides tools for parsing and validating elfpkg.toml manifests.
"""

import os
import re
from dataclasses import dataclass, field
from pathlib import Path
from typing import Dict, List, Optional, Set, Union
import toml

# Import semver for version handling
try:
    import semver
except ImportError:
    raise ImportError("semver package is required. Install with: pip install semver")


class ManifestError(Exception):
    """Exception raised for manifest parsing and validation errors."""
    pass


@dataclass
class Dependency:
    """Represents a package dependency with version constraints."""
    name: str
    version_req: str
    optional: bool = False
    features: List[str] = field(default_factory=list)
    
    def __post_init__(self):
        # Validate version requirement is a valid semver range
        try:
            # Check if the semver range is valid
            semver.VersionInfo.parse(self.version_req.lstrip("^~=<>"))
        except ValueError as e:
            raise ManifestError(f"Invalid version requirement for {self.name}: {e}")
    
    def matches(self, version: str) -> bool:
        """Check if a version matches this dependency's requirements."""
        try:
            version_info = semver.VersionInfo.parse(version)
            return version_info.match(self.version_req)
        except ValueError:
            return False


@dataclass
class Manifest:
    """
    Represents an ELFIN package manifest (elfpkg.toml).
    
    This is the primary configuration for an ELFIN package, similar to
    Cargo.toml in Rust or package.json in Node.js.
    """
    name: str
    version: str
    authors: List[str] = field(default_factory=list)
    edition: str = "elfin-1.0"
    description: Optional[str] = None
    license: Optional[str] = None
    dependencies: Dict[str, Dependency] = field(default_factory=dict)
    dev_dependencies: Dict[str, Dependency] = field(default_factory=dict)
    solver: Dict[str, str] = field(default_factory=dict)
    features: Dict[str, List[str]] = field(default_factory=dict)
    
    @classmethod
    def load(cls, path: Path = Path("elfpkg.toml")) -> "Manifest":
        """
        Load and parse a manifest file.
        
        Args:
            path: Path to the elfpkg.toml file
            
        Returns:
            Parsed Manifest object
            
        Raises:
            ManifestError: If the manifest is invalid or can't be parsed
            FileNotFoundError: If the manifest file doesn't exist
        """
        try:
            data = toml.load(path)
        except toml.TomlDecodeError as e:
            raise ManifestError(f"Failed to parse {path}: {e}")
        except FileNotFoundError:
            raise FileNotFoundError(f"Manifest file not found: {path}")
        
        # Validate required sections
        if "package" not in data:
            raise ManifestError("Missing [package] section in manifest")
        
        pkg = data["package"]
        
        # Validate required fields
        for field in ["name", "version"]:
            if field not in pkg:
                raise ManifestError(f"Missing required field '{field}' in [package] section")
        
        # Validate package name format
        name = pkg["name"]
        if not re.match(r'^[a-zA-Z][\w\-]*$', name):
            raise ManifestError(f"Invalid package name: {name}. Must start with a letter and contain only letters, numbers, underscores, and hyphens.")
        
        # Parse dependencies
        dependencies = {}
        for dep_name, dep_req in data.get("dependencies", {}).items():
            if isinstance(dep_req, str):
                # Simple version requirement
                dependencies[dep_name] = Dependency(dep_name, dep_req)
            elif isinstance(dep_req, dict):
                # Complex dependency with features, etc.
                dependencies[dep_name] = Dependency(
                    dep_name,
                    dep_req.get("version", "*"),
                    optional=dep_req.get("optional", False),
                    features=dep_req.get("features", [])
                )
            else:
                raise ManifestError(f"Invalid dependency specification for {dep_name}")
        
        # Parse dev dependencies
        dev_dependencies = {}
        for dep_name, dep_req in data.get("dev_dependencies", {}).items():
            if isinstance(dep_req, str):
                # Simple version requirement
                dev_dependencies[dep_name] = Dependency(dep_name, dep_req)
            elif isinstance(dep_req, dict):
                # Complex dependency with features, etc.
                dev_dependencies[dep_name] = Dependency(
                    dep_name,
                    dep_req.get("version", "*"),
                    optional=dep_req.get("optional", False),
                    features=dep_req.get("features", [])
                )
            else:
                raise ManifestError(f"Invalid dev-dependency specification for {dep_name}")
        
        # Parse features
        features = data.get("features", {})
        
        # Create the manifest object
        return cls(
            name=name,
            version=pkg["version"],
            authors=pkg.get("authors", []),
            edition=pkg.get("edition", "elfin-1.0"),
            description=pkg.get("description"),
            license=pkg.get("license"),
            dependencies=dependencies,
            dev_dependencies=dev_dependencies,
            solver=data.get("solver", {}),
            features=features
        )
    
    def save(self, path: Path = Path("elfpkg.toml")) -> None:
        """
        Save the manifest to a file.
        
        Args:
            path: Path to save the elfpkg.toml file
            
        Raises:
            ManifestError: If the manifest can't be saved
        """
        # Convert the manifest to a dict structure
        data = {
            "package": {
                "name": self.name,
                "version": self.version,
                "edition": self.edition
            }
        }
        
        # Add optional package fields
        if self.authors:
            data["package"]["authors"] = self.authors
        if self.description:
            data["package"]["description"] = self.description
        if self.license:
            data["package"]["license"] = self.license
        
        # Add dependencies
        if self.dependencies:
            data["dependencies"] = {}
            for dep_name, dep in self.dependencies.items():
                if dep.features or dep.optional:
                    # Complex dependency
                    data["dependencies"][dep_name] = {
                        "version": dep.version_req
                    }
                    if dep.features:
                        data["dependencies"][dep_name]["features"] = dep.features
                    if dep.optional:
                        data["dependencies"][dep_name]["optional"] = True
                else:
                    # Simple dependency
                    data["dependencies"][dep_name] = dep.version_req
        
        # Add dev dependencies
        if self.dev_dependencies:
            data["dev_dependencies"] = {}
            for dep_name, dep in self.dev_dependencies.items():
                if dep.features or dep.optional:
                    # Complex dependency
                    data["dev_dependencies"][dep_name] = {
                        "version": dep.version_req
                    }
                    if dep.features:
                        data["dev_dependencies"][dep_name]["features"] = dep.features
                    if dep.optional:
                        data["dev_dependencies"][dep_name]["optional"] = True
                else:
                    # Simple dependency
                    data["dev_dependencies"][dep_name] = dep.version_req
        
        # Add solver configuration
        if self.solver:
            data["solver"] = self.solver
        
        # Add features
        if self.features:
            data["features"] = self.features
        
        # Write to file
        try:
            with open(path, 'w') as f:
                toml.dump(data, f)
        except Exception as e:
            raise ManifestError(f"Failed to save manifest to {path}: {e}")
    
    def check_semver(self, current: str) -> bool:
        """
        Check if current version is compatible with this manifest.
        
        Args:
            current: Current version string
            
        Returns:
            True if compatible, False otherwise
            
        Raises:
            ValueError: If versions can't be parsed
        """
        try:
            current_version = semver.VersionInfo.parse(current)
            package_version = semver.VersionInfo.parse(self.version)
            
            # Major version must match for versions >= 1.0.0
            if package_version.major >= 1:
                return current_version.major == package_version.major
            
            # For versions < 1.0.0, minor must match
            return (current_version.major == package_version.major and 
                    current_version.minor == package_version.minor)
        except ValueError as e:
            raise ValueError(f"Failed to parse version: {e}")
    
    def get_env_vars(self) -> Dict[str, str]:
        """
        Get environment variables used in the manifest.
        
        This extracts environment variables referenced in solver settings
        and other fields, allowing resolution of values like ${HOME}/mosek.lic
        
        Returns:
            Dictionary of environment variable names to their values
        """
        env_vars = {}
        
        # Check solver settings for environment variables
        for key, value in self.solver.items():
            if isinstance(value, str):
                env_var_matches = re.findall(r'\${([^}]+)}', value)
                for var_name in env_var_matches:
                    if var_name in os.environ:
                        env_vars[var_name] = os.environ[var_name]
        
        return env_vars
    
    def resolve_env_vars(self) -> Dict[str, str]:
        """
        Resolve all environment variables in the manifest.
        
        Returns:
            Dictionary with resolved solver settings
        """
        resolved = {}
        
        # Resolve solver settings
        for key, value in self.solver.items():
            if isinstance(value, str):
                # Replace ${VAR} with environment variable value
                for var_name, var_value in self.get_env_vars().items():
                    value = value.replace(f"${{{var_name}}}", var_value)
                resolved[key] = value
            else:
                resolved[key] = value
        
        return resolved

import React, { useRef, useEffect, useState } from 'react';
import { useSSE } from '../hooks/useSSE';

interface SafetyTimelineProps {
  sysId: string;
  thresholdColor?: string;
  barrierColor?: string;
  width?: number;
  height?: number;
  maxPoints?: number;
}

interface BarrierData {
  t: number;
  barrier: number;
  thr: number;
}

/**
 * Component to display real-time barrier function values over time.
 * Uses Server-Sent Events to stream data at high frequency (20Hz).
 */
const SafetyTimeline: React.FC<SafetyTimelineProps> = ({
  sysId,
  thresholdColor = 'rgba(255, 0, 0, 0.5)',
  barrierColor = 'rgba(0, 123, 255, 0.8)',
  width = 800,
  height = 300,
  maxPoints = 100
}) => {
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const [points, setPoints] = useState<BarrierData[]>([]);
  const [alertActive, setAlertActive] = useState(false);
  const alertTimeoutRef = useRef<NodeJS.Timeout | null>(null);

  // Subscribe to barrier function stream using SSE
  const { data, status } = useSSE<BarrierData>(
    `/api/v1/stream/barrier?sys=${sysId}`,
    null,
    {
      onMessage: (newData) => {
        // Check if barrier exceeds threshold
        if (newData.barrier > newData.thr) {
          // Activate alert
          setAlertActive(true);
          
          // Clear existing timeout if there is one
          if (alertTimeoutRef.current) {
            clearTimeout(alertTimeoutRef.current);
          }
          
          // Set timeout to clear alert after 500ms
          alertTimeoutRef.current = setTimeout(() => {
            setAlertActive(false);
            alertTimeoutRef.current = null;
          }, 500);
        }
        
        // Update points for timeline
        setPoints(prev => {
          const newPoints = [...prev, newData];
          // Keep only the most recent points if we exceed maxPoints
          return newPoints.length > maxPoints
            ? newPoints.slice(newPoints.length - maxPoints)
            : newPoints;
        });
      }
    }
  );

  // Draw timeline on canvas
  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;
    
    const ctx = canvas.getContext('2d');
    if (!ctx) return;
    
    // Clear canvas
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    
    if (points.length === 0) return;
    
    // Calculate min and max for y-axis
    let minBarrier = Math.min(...points.map(p => p.barrier));
    let maxBarrier = Math.max(...points.map(p => p.barrier));
    const minThr = Math.min(...points.map(p => p.thr));
    const maxThr = Math.max(...points.map(p => p.thr));
    
    // Ensure min and max include both barrier and threshold values
    minBarrier = Math.min(minBarrier, minThr);
    maxBarrier = Math.max(maxBarrier, maxThr);
    
    // Add some padding to prevent values from being drawn at edges
    const paddingFactor = 0.1;
    const range = maxBarrier - minBarrier;
    const padding = range * paddingFactor;
    const yMin = minBarrier - padding;
    const yMax = maxBarrier + padding;
    
    // Function to map time and barrier values to canvas coordinates
    const mapX = (t: number, tMin: number, tMax: number) => 
      ((t - tMin) / (tMax - tMin)) * canvas.width;
    const mapY = (v: number) => 
      canvas.height - ((v - yMin) / (yMax - yMin)) * canvas.height;
    
    // Get time range
    const tMin = points[0].t;
    const tMax = points[points.length - 1].t;
    
    // Draw threshold line
    ctx.beginPath();
    ctx.moveTo(0, mapY(points[0].thr));
    for (let i = 0; i < points.length; i++) {
      ctx.lineTo(mapX(points[i].t, tMin, tMax), mapY(points[i].thr));
    }
    ctx.strokeStyle = thresholdColor;
    ctx.lineWidth = 2;
    ctx.stroke();
    
    // Draw barrier function line
    ctx.beginPath();
    ctx.moveTo(0, mapY(points[0].barrier));
    for (let i = 0; i < points.length; i++) {
      ctx.lineTo(mapX(points[i].t, tMin, tMax), mapY(points[i].barrier));
    }
    ctx.strokeStyle = barrierColor;
    ctx.lineWidth = 2;
    ctx.stroke();
    
    // Draw axes
    ctx.beginPath();
    ctx.moveTo(0, canvas.height);
    ctx.lineTo(canvas.width, canvas.height);
    ctx.strokeStyle = '#333';
    ctx.lineWidth = 1;
    ctx.stroke();
    
    // Draw y-axis
    ctx.beginPath();
    ctx.moveTo(0, 0);
    ctx.lineTo(0, canvas.height);
    ctx.stroke();
    
    // Draw y-axis labels
    ctx.fillStyle = '#333';
    ctx.textAlign = 'right';
    ctx.textBaseline = 'middle';
    ctx.font = '12px Arial';
    
    // Draw min and max y values
    ctx.fillText(yMin.toFixed(2), 30, canvas.height - 10);
    ctx.fillText(yMax.toFixed(2), 30, 10);
    
    // Draw alert overlay if barrier has exceeded threshold
    if (alertActive) {
      ctx.fillStyle = 'rgba(255, 0, 0, 0.2)';
      ctx.fillRect(0, 0, canvas.width, canvas.height);
    }
    
  }, [points, thresholdColor, barrierColor, alertActive]);
  
  return (
    <div className="safety-timeline" style={{ position: 'relative' }}>
      <h3>Barrier Function Timeline</h3>
      <div>Status: {status}</div>
      {alertActive && (
        <div 
          style={{ 
            position: 'absolute', 
            top: 0, 
            left: 0, 
            right: 0, 
            padding: '5px',
            backgroundColor: 'rgba(255, 0, 0, 0.8)',
            color: 'white',
            fontWeight: 'bold',
            textAlign: 'center',
            zIndex: 100
          }}
        >
          SAFETY VIOLATION: Barrier threshold exceeded!
        </div>
      )}
      <canvas 
        ref={canvasRef} 
        width={width} 
        height={height}
        style={{ 
          border: '1px solid #ddd',
          backgroundColor: '#f9f9f9'
        }}
      />
      <div className="legend" style={{ display: 'flex', marginTop: '10px' }}>
        <div style={{ marginRight: '20px' }}>
          <span 
            style={{ 
              display: 'inline-block', 
              width: '20px', 
              height: '3px', 
              backgroundColor: barrierColor,
              marginRight: '5px'
            }} 
          />
          <span>Barrier Value</span>
        </div>
        <div>
          <span 
            style={{ 
              display: 'inline-block', 
              width: '20px', 
              height: '3px', 
              backgroundColor: thresholdColor,
              marginRight: '5px'
            }} 
          />
          <span>Threshold</span>
        </div>
      </div>
    </div>
  );
};

export default SafetyTimeline;

import React, { useRef, useEffect, useState } from 'react';
import * as THREE from 'three';
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls';
import { GLTFLoader } from 'three/examples/jsm/loaders/GLTFLoader';

interface IsoSurfaceViewerProps {
  levelValue: number;
  width?: number;
  height?: number;
  functionType?: 'barrier' | 'lyapunov';
  backgroundColor?: string;
  surfaceColor?: string;
  wireframe?: boolean;
}

/**
 * Component to display 3D iso-surfaces for barrier or Lyapunov functions.
 * Uses three.js for WebGL-based rendering.
 */
const IsoSurfaceViewer: React.FC<IsoSurfaceViewerProps> = ({
  levelValue,
  width = 500,
  height = 500,
  functionType = 'barrier',
  backgroundColor = '#f0f0f0',
  surfaceColor = '#1e88e5',
  wireframe = false
}) => {
  const mountRef = useRef<HTMLDivElement>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  
  // Store three.js objects in refs to clean up on unmount
  const sceneRef = useRef<THREE.Scene | null>(null);
  const cameraRef = useRef<THREE.PerspectiveCamera | null>(null);
  const rendererRef = useRef<THREE.WebGLRenderer | null>(null);
  const controlsRef = useRef<OrbitControls | null>(null);
  const meshRef = useRef<THREE.Mesh | null>(null);
  
  // Set up three.js scene
  useEffect(() => {
    if (!mountRef.current) return;
    
    // Create scene
    const scene = new THREE.Scene();
    sceneRef.current = scene;
    scene.background = new THREE.Color(backgroundColor);
    
    // Create camera
    const camera = new THREE.PerspectiveCamera(75, width / height, 0.1, 1000);
    cameraRef.current = camera;
    camera.position.z = 5;
    
    // Create renderer
    const renderer = new THREE.WebGLRenderer({ antialias: true });
    rendererRef.current = renderer;
    renderer.setSize(width, height);
    renderer.setPixelRatio(window.devicePixelRatio);
    mountRef.current.appendChild(renderer.domElement);
    
    // Add orbit controls
    const controls = new OrbitControls(camera, renderer.domElement);
    controlsRef.current = controls;
    controls.enableDamping = true;
    controls.dampingFactor = 0.05;
    
    // Add lights
    const ambientLight = new THREE.AmbientLight(0xffffff, 0.5);
    scene.add(ambientLight);
    
    const directionalLight = new THREE.DirectionalLight(0xffffff, 1.0);
    directionalLight.position.set(5, 5, 5);
    scene.add(directionalLight);
    
    // Add coordinate axes for reference
    const axesHelper = new THREE.AxesHelper(2);
    scene.add(axesHelper);
    
    // Animation loop
    const animate = () => {
      requestAnimationFrame(animate);
      if (controlsRef.current) {
        controlsRef.current.update();
      }
      renderer.render(scene, camera);
    };
    
    animate();
    
    // Clean up on unmount
    return () => {
      if (mountRef.current && rendererRef.current) {
        mountRef.current.removeChild(rendererRef.current.domElement);
      }
      if (meshRef.current && sceneRef.current) {
        sceneRef.current.remove(meshRef.current);
        meshRef.current.geometry.dispose();
        if (Array.isArray(meshRef.current.material)) {
          meshRef.current.material.forEach(material => material.dispose());
        } else {
          meshRef.current.material.dispose();
        }
      }
      if (rendererRef.current) {
        rendererRef.current.dispose();
      }
    };
  }, [width, height, backgroundColor]);
  
  // Load iso-surface mesh when level value changes
  useEffect(() => {
    if (!sceneRef.current) return;
    
    setLoading(true);
    setError(null);
    
    // Remove existing mesh if any
    if (meshRef.current && sceneRef.current) {
      sceneRef.current.remove(meshRef.current);
      meshRef.current.geometry.dispose();
      if (Array.isArray(meshRef.current.material)) {
        meshRef.current.material.forEach(material => material.dispose());
      } else {
        meshRef.current.material.dispose();
      }
      meshRef.current = null;
    }
    
    // Fetch iso-surface mesh
    const loader = new GLTFLoader();
    const url = `/api/v1/barrier/isosurface?type=${functionType}&lvl=${levelValue}`;
    
    loader.load(
      url,
      (gltf) => {
        // Process loaded GLTF model
        const model = gltf.scene;
        
        // Apply material to all meshes
        model.traverse((child) => {
          if (child instanceof THREE.Mesh) {
            child.material = new THREE.MeshPhongMaterial({
              color: new THREE.Color(surfaceColor),
              wireframe,
              side: THREE.DoubleSide,
              flatShading: true,
            });
          }
        });
        
        // Center model
        const box = new THREE.Box3().setFromObject(model);
        const center = box.getCenter(new THREE.Vector3());
        model.position.sub(center);
        
        // Scale model to fit view
        const size = box.getSize(new THREE.Vector3());
        const maxDim = Math.max(size.x, size.y, size.z);
        const scale = 3 / maxDim;
        model.scale.multiplyScalar(scale);
        
        // Add to scene
        if (sceneRef.current) {
          sceneRef.current.add(model);
          meshRef.current = model.children[0] as THREE.Mesh;
        }
        
        setLoading(false);
      },
      (progress) => {
        // Handle load progress if needed
      },
      (error) => {
        setError('Failed to load iso-surface model.');
        setLoading(false);
        console.error('Error loading GLTF model:', error);
      }
    );
  }, [levelValue, functionType, surfaceColor, wireframe]);
  
  return (
    <div className="iso-surface-viewer">
      <h3>{functionType === 'barrier' ? 'Barrier' : 'Lyapunov'} Function Iso-Surface</h3>
      <div style={{ position: 'relative' }}>
        <div ref={mountRef}></div>
        {loading && (
          <div 
            style={{ 
              position: 'absolute', 
              top: 0, 
              left: 0, 
              width: '100%', 
              height: '100%',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              backgroundColor: 'rgba(255, 255, 255, 0.7)'
            }}
          >
            <div>Loading iso-surface...</div>
          </div>
        )}
        {error && (
          <div 
            style={{ 
              position: 'absolute', 
              top: 0, 
              left: 0, 
              width: '100%', 
              height: '100%',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              backgroundColor: 'rgba(255, 0, 0, 0.1)',
              color: 'red'
            }}
          >
            <div>{error}</div>
          </div>
        )}
      </div>
      <div className="info" style={{ marginTop: '10px' }}>
        <div>Level: {levelValue.toFixed(2)}</div>
        <div>Type: {functionType}</div>
        <div style={{ marginTop: '5px', fontSize: '0.8em', color: '#666' }}>
          Use mouse to rotate, scroll to zoom, shift+click to pan
        </div>
      </div>
    </div>
  );
};

export default IsoSurfaceViewer;

import React, { useState, useEffect, useCallback } from 'react';

interface ParamSliderProps {
  label: string;
  min: number;
  max: number;
  step: number;
  initialValue: number;
  paramId: string;
  onValueChange?: (value: number) => void;
  debounceTime?: number;
}

/**
 * Component for adjusting parameters with debounced API updates.
 * Supports real-time control of Lyapunov/Barrier parameters with backend updates.
 */
const ParamSlider: React.FC<ParamSliderProps> = ({
  label,
  min,
  max,
  step,
  initialValue,
  paramId,
  onValueChange,
  debounceTime = 100
}) => {
  const [value, setValue] = useState<number>(initialValue);
  const [displayValue, setDisplayValue] = useState<string>(initialValue.toString());
  const [isSending, setIsSending] = useState<boolean>(false);
  const [lastSent, setLastSent] = useState<number | null>(null);
  
  // Debounced API call to update parameter
  const updateParameter = useCallback(
    async (newValue: number) => {
      setIsSending(true);
      try {
        const response = await fetch('/api/v1/koopman/params', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({ [paramId]: newValue })
        });
        
        if (!response.ok) {
          throw new Error(`API error: ${response.status}`);
        }
        
        setLastSent(newValue);
      } catch (error) {
        console.error('Failed to update parameter:', error);
      } finally {
        setIsSending(false);
      }
    },
    [paramId]
  );
  
  // Debounce parameter updates
  useEffect(() => {
    const handler = setTimeout(() => {
      if (value !== lastSent) {
        updateParameter(value);
        if (onValueChange) {
          onValueChange(value);
        }
      }
    }, debounceTime);
    
    return () => {
      clearTimeout(handler);
    };
  }, [value, lastSent, updateParameter, onValueChange, debounceTime]);
  
  // Handle slider change
  const handleSliderChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const newValue = parseFloat(event.target.value);
    setValue(newValue);
    setDisplayValue(newValue.toFixed(step < 1 ? -Math.floor(Math.log10(step)) : 0));
  };
  
  // Handle direct input change
  const handleInputChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setDisplayValue(event.target.value);
  };
  
  // Process input on blur
  const handleInputBlur = () => {
    let newValue = parseFloat(displayValue);
    
    if (isNaN(newValue)) {
      newValue = initialValue;
    } else {
      newValue = Math.max(min, Math.min(max, newValue));
    }
    
    setValue(newValue);
    setDisplayValue(newValue.toFixed(step < 1 ? -Math.floor(Math.log10(step)) : 0));
  };
  
  // Handle input keypress (Enter)
  const handleKeyPress = (event: React.KeyboardEvent<HTMLInputElement>) => {
    if (event.key === 'Enter') {
      handleInputBlur();
    }
  };
  
  // Format number for display with appropriate decimal places
  const getDisplayValue = useCallback((val: number): string => {
    return val.toFixed(step < 1 ? -Math.floor(Math.log10(step)) : 0);
  }, [step]);
  
  return (
    <div className="param-slider" style={{ margin: '10px 0', width: '100%' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '5px' }}>
        <label style={{ fontSize: '0.9rem', fontWeight: 'bold' }}>
          {label}
        </label>
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <input
            type="number"
            value={displayValue}
            onChange={handleInputChange}
            onBlur={handleInputBlur}
            onKeyPress={handleKeyPress}
            min={min}
            max={max}
            step={step}
            style={{
              width: '70px',
              marginLeft: '10px',
              padding: '4px',
              border: '1px solid #ccc',
              borderRadius: '4px',
              textAlign: 'right'
            }}
          />
          {isSending && (
            <div 
              style={{ 
                marginLeft: '10px', 
                width: '10px', 
                height: '10px',
                borderRadius: '50%',
                backgroundColor: '#ffa500'
              }}
            />
          )}
        </div>
      </div>
      <div style={{ display: 'flex', alignItems: 'center' }}>
        <div style={{ marginRight: '10px', fontSize: '0.8rem' }}>{min}</div>
        <input
          type="range"
          min={min}
          max={max}
          step={step}
          value={value}
          onChange={handleSliderChange}
          style={{ flex: 1 }}
        />
        <div style={{ marginLeft: '10px', fontSize: '0.8rem' }}>{max}</div>
      </div>
      <div 
        style={{ 
          display: 'flex', 
          justifyContent: 'space-between', 
          marginTop: '3px',
          fontSize: '0.7rem',
          color: '#666'
        }}
      >
        <div>Current: {getDisplayValue(value)}</div>
        {lastSent !== null && (
          <div>Last sent: {getDisplayValue(lastSent)}</div>
        )}
      </div>
    </div>
  );
};

