  params: {
    # Desired position
    x_d: 0.0;
    y_d: 0.0;
    z_d: 5.0;

    # Desired velocity
    vx_d: 0.0;
    vy_d: 0.0;
    vz_d: 0.0;

    # Desired yaw
    psi_d: 0.0;

    # Position gains
    kp_x: 2.0;
    kp_y: 2.0;
