    # Velocity gains
    kd_x: 1.0;
    kd_y: 1.0;
    kd_z: 1.0;

    # Attitude gains
    kp_phi: 10.0;
    kp_theta: 10.0;
    kp_psi: 5.0;

    # Angular rate gains
    kd_phi: 2.0;
    kd_theta: 2.0;
    kd_psi: 1.0;
  };
}

