# Define barrier function for joint limits
barrier JointLimitsBarrier {
  system: Manipulator;

  # Safety constraint: joint limits
  # This barrier is positive when joints are within limits
  # B > 0 means safe, B = 0 at boundary, B < 0 means unsafe (joint limit violation)
  # We use a product of barrier functions for upper and lower limits
  B: (q1_max - q1)*(q1 - q1_min) * 
     (q2_max - q2)*(q2 - q2_min) * 
     (q3_max - q3)*(q3 - q3_min) * 
     (q4_max - q4)*(q4 - q4_min) * 
     (q5_max - q5)*(q5 - q5_min) * 
     (q6_max - q6)*(q6 - q6_min);

