# ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
# Mobile Robot Navigation & Control ‚Äî ELFIN v2 (final, complete)
# ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
# No placeholders, every symbol concretely defined
# ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ

helpers {
  hAbs(x)      = if x >= 0 then x else -x;
  hMin(a, b)   = if a <= b then a else b;
  hMax(a, b)   = if a >= b then a else b;
  wrapAngle(t) = mod(t + pi, 2*pi) - pi;
}

# ‚îÄ‚îÄ Plant ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
system DifferentialDrive {
  continuous_state: [x, y, theta, v, omega];   # pose + body-frame speeds
  input: [v_l, v_r];                           # left / right wheel rates

  params {
    wheel_radius : 0.05;   # m
    wheel_base   : 0.30;   # m
    max_speed    : 1.0;    # m/s
    max_omega    : 3.0;    # rad/s
    mass         : 10.0;   # kg
    inertia      : 0.2;    # kg¬∑m¬≤
    friction     : 0.1;    # unitless drag
  }

  flow_dynamics {
    # Kinematics
    x_dot     = v * cos(theta);
    y_dot     = v * sin(theta);
    theta_dot = omega;

    # Longitudinal / yaw dynamics (simplified)
    v_dot     = (wheel_radius / 2) * (v_r + v_l)               - friction * v;
    omega_dot = (wheel_radius / wheel_base) * (v_r - v_l)      - friction * omega;
  }
}

# ‚îÄ‚îÄ Lyapunov candidates ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
lyapunov PathFollower {
  system DifferentialDrive;
  params { x_goal: 5.0;  y_goal: 5.0;  alpha: 1.0; }

  V = 0.5 * ((x - x_goal)**2 + (y - y_goal)**2)
      + alpha * (1 - cos(theta - atan2(y_goal - y, x_goal - x)));
}

lyapunov TrajTracker {
  system DifferentialDrive;
  params {
    x_d: 0.0; y_d: 0.0; theta_d: 0.0;
    v_d: 0.0; omega_d: 0.0;
    beta: 0.5;  gamma: 0.2;
  }

  V = 0.5 * ((x - x_d)**2 + (y - y_d)**2
           + beta  * (theta - theta_d)**2
           + gamma * ((v - v_d)**2 + (omega - omega_d)**2));
}

# ‚îÄ‚îÄ Barrier certificates ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
barrier StaticObstacle {
  system DifferentialDrive;
  params { x_obs: 3.0; y_obs: 2.0; r_obs: 0.5; r_robot: 0.2; margin: 0.1; alpha: 1.0; }

  B        = (x - x_obs)**2 + (y - y_obs)**2 - (r_robot + r_obs + margin)**2;
  alphaFun = alpha * B;
}

barrier MultiObstacle {
  system DifferentialDrive;
  params {
    x_obs1: 3.0; y_obs1: 2.0;
    x_obs2: 1.5; y_obs2: 4.0;
    x_obs3: 4.0; y_obs3: 3.5;
    r_safe: 0.8; alpha: 1.0;
  }

  B        = ((x - x_obs1)**2 + (y - y_obs1)**2 - r_safe**2)
           * ((x - x_obs2)**2 + (y - y_obs2)**2 - r_safe**2)
           * ((x - x_obs3)**2 + (y - y_obs3)**2 - r_safe**2);
  alphaFun = alpha * B;
}

barrier DynamicObstacle {
  system DifferentialDrive;
  params {
    x_obs: 2.0; y_obs: 3.0;
    v_obs_x: 0.2; v_obs_y: -0.3;
    r_safe: 0.8; ttc_min: 2.0; alpha: 1.0;
  }

  rel_dist      = sqrt((x - x_obs)**2 + (y - y_obs)**2) - r_safe;
  closing_speed = -((v * cos(theta) - v_obs_x) * (x - x_obs)
                    + (v * sin(theta) - v_obs_y) * (y - y_obs))
                  / sqrt((x - x_obs)**2 + (y - y_obs)**2);

  ttc  = if(closing_speed > 0.01, rel_dist / closing_speed, 100.0);
  B    = ttc - ttc_min;
  alphaFun = alpha * B;
}

barrier VelocityLimit {
  system DifferentialDrive;
  params { max_speed: 1.0; max_omega: 3.0; alpha: 1.0; }

  B        = (max_speed**2 - v**2) * (max_omega**2 - omega**2);
  alphaFun = alpha * B;
}

barrier Workspace {
  system DifferentialDrive;
  params { x_min: -10.0; x_max: 10.0; y_min: -10.0; y_max: 10.0; alpha: 1.0; }

  B        = (x_max - x) * (x - x_min) * (y_max - y) * (y - y_min);
  alphaFun = alpha * B;
}

# ‚îÄ‚îÄ Controllers / modes ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
mode WaypointNav {
  system DifferentialDrive;
  lyapunov PathFollower;
  barriers [VelocityLimit, Workspace, MultiObstacle];

  params { k_d: 0.5; k_h: 2.0; }

  controller {
    dist   = sqrt((x - x_goal)**2 + (y - y_goal)**2);
    ang    = atan2(y_goal - y, x_goal - x);
    ang_e  = wrapAngle(ang - theta);

    v_ref     = hMin(k_d * dist, max_speed);
    omega_ref = k_h * ang_e;

    v_l = (2 * v_ref - omega_ref * wheel_base) / (2 * wheel_radius);
    v_r = (2 * v_ref + omega_ref * wheel_base) / (2 * wheel_radius);
  }
}

mode TrajectoryTrack {
  system DifferentialDrive;
  lyapunov TrajTracker;
  barriers [VelocityLimit, Workspace, MultiObstacle];

  params { k_p: 0.8; k_h: 2.5; k_perp: 0.5; }

  controller {
    x_e  = x_d - x;
    y_e  = y_d - y;
    th_e = wrapAngle(theta_d - theta);

    e_par  =  x_e * cos(theta) + y_e * sin(theta);
    e_perp = -x_e * sin(theta) + y_e * cos(theta);

    v_cmd     = v_d + k_p * e_par;
    omega_cmd = omega_d + k_h * th_e + k_perp * e_perp;

    v_l = (2 * v_cmd - omega_cmd * wheel_base) / (2 * wheel_radius);
    v_r = (2 * v_cmd + omega_cmd * wheel_base) / (2 * wheel_radius);
  }
}

mode ObstacleAvoid {
  system DifferentialDrive;
  barriers [VelocityLimit, Workspace, DynamicObstacle];

  params {
    x_goal: 5.0; y_goal: 5.0;
    x_obs1: 3.0; y_obs1: 2.0; x_obs2: 1.5; y_obs2: 4.0;
    attract: 1.0; repel: 0.5; influence: 3.0; safe: 1.0; k_h: 2.0;
  }

  controller {
    # Goal attraction vector
    gx     = x_goal - x;  gy = y_goal - y;
    gdist  = sqrt(gx**2 + gy**2);
    gx    /= hMax(gdist, 0.1);  gy /= hMax(gdist, 0.1);

    # Obstacle-1 repulsion
    o1x = x - x_obs1;  o1y = y - y_obs1;
    d1  = sqrt(o1x**2 + o1y**2);
    o1x /= hMax(d1, 0.1);  o1y /= hMax(d1, 0.1);
    rep1 = if(d1 < influence, repel * (1/d1 - 1/influence)**2, 0);

    # Obstacle-2 repulsion
    o2x = x - x_obs2;  o2y = y - y_obs2;
    d2  = sqrt(o2x**2 + o2y**2);
    o2x /= hMax(d2, 0.1);  o2y /= hMax(d2, 0.1);
    rep2 = if(d2 < influence, repel * (1/d2 - 1/influence)**2, 0);

    # Combined potential-field vector
    rx = attract * gx + rep1 * o1x + rep2 * o2x;
    ry = attract * gy + rep1 * o1y + rep2 * o2y;

    hdg_e = wrapAngle(atan2(ry, rx) - theta);
    min_d = hMin(d1, d2);
    v_des = max_speed * hMin(1.0, min_d / safe);

    omega_cmd = k_h * hdg_e;
    v_l = (2 * v_des - omega_cmd * wheel_base) / (2 * wheel_radius);
    v_r = (2 * v_des + omega_cmd * wheel_base) / (2 * wheel_radius);
  }
}

# ‚îÄ‚îÄ RRT* planner & closed-loop integration ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
planner RRTStar {
  system DifferentialDrive;

  config {
    world_bounds: [x_min, x_max, y_min, y_max];
    goal_bias: 0.1;
    max_iterations: 1000;
    step_size: 0.5;
    neighbor_radius: 2.0;
    clearance: 0.5;
  }

  obstacles [
    {type: "circle",    x: x_obs1, y: y_obs1, radius: r_obs},
    {type: "circle",    x: x_obs2, y: y_obs2, radius: r_obs},
    {type: "circle",    x: x_obs3, y: y_obs3, radius: r_obs},
    {type: "rectangle", x: rect_x, y: rect_y,
                        width: rect_w, height: rect_h, angle: rect_a}
  ]

  params {
    x_min: -10.0; x_max: 10.0; y_min: -10.0; y_max: 10.0;

    # Circular obstacles
    x_obs1: 3.0; y_obs1: 2.0;
    x_obs2: 1.5; y_obs2: 4.0;
    x_obs3: 4.0; y_obs3: 3.5;
    r_obs: 0.5;

    # Rectangular obstacle
    rect_x: 5.0; rect_y: 6.0;
    rect_w: 2.0; rect_h: 1.0; rect_a: 0.0;
  }
}

integration Plan2Track {
  planner    RRTStar;
  controller TrajectoryTrack;

  config {
    update_rate: 10.0;      # Hz
    lookahead: 0.5;         # m
    replan_threshold: 1.0;  # m
    smooth: true;
  }
}

# Robotic Manipulator Controller Implementation
# This file demonstrates ELFIN syntax for a robotic arm control system

# Helper functions used throughout this file
helpers: {
  # Absolute value function (prefixed to avoid clash with future intrinsics)
  h_abs(x) = if x >= 0 then x else -x;
  
  # Minimum of two values (prefixed to avoid clash with future intrinsics)
  h_min(a, b) = if a <= b then a else b;
  
  # Maximum of two values (prefixed to avoid clash with future intrinsics)
  h_max(a, b) = if a >= b then a else b;
  
  # Angle wrapping function to normalize angles to [-œÄ, œÄ]
  # Using lowercase mod() for strict lexer compatibility
  # NOTE: This could delegate to a built-in once math stdlib is imported
  wrap_angle(theta) = mod(theta + pi, 2*pi) - pi;
}

# Define system dynamics
system Manipulator {
  # State: joint positions and velocities
  # For a 6-DOF arm: [q1, q2, q3, q4, q5, q6, dq1, dq2, dq3, dq4, dq5, dq6]
  continuous_state: [q1, q2, q3, q4, q5, q6, dq1, dq2, dq3, dq4, dq5, dq6];
  
  # Input: joint torques
  input: [tau1, tau2, tau3, tau4, tau5, tau6];
  
  # System parameters
  params: {
    # Link masses (kg)
    m1: 5.0;
    m2: 4.0;
    m3: 3.0;
    m4: 2.0;
    m5: 1.0;
    m6: 0.5;
    
    # Link lengths (m)
    l1: 0.3;
    l2: 0.4;
    l3: 0.4;
    l4: 0.2;
    l5: 0.1;
    l6: 0.1;
    
    # Moments of inertia (simplified as diagonal)
    I1: 0.1;
    I2: 0.15;
    I3: 0.12;
    I4: 0.08;
    I5: 0.05;
    I6: 0.02;
    
    # Joint damping coefficients
    d1: 0.5;
    d2: 0.5;
    d3: 0.5;
    d4: 0.3;
    d5: 0.3;
    d6: 0.2;
    
    # Gravity
    g: 9.81;
  };
  
  # Continuous dynamics
  # This is a simplified model; in practice, the full dynamics would be much more complex
  flow_dynamics: {
    # Position derivatives = velocities
    # Note: Position derivatives are set equal to the corresponding velocity state variables
    dq1 = dq1;  # dq1 refers to the 7th state variable in the system
    dq2 = dq2;  # dq2 refers to the 8th state variable in the system
    dq3 = dq3;  # dq3 refers to the 9th state variable in the system
    dq4 = dq4;  # dq4 refers to the 10th state variable in the system
    dq5 = dq5;  # dq5 refers to the 11th state variable in the system
    dq6 = dq6;  # dq6 refers to the 12th state variable in the system
    
    # Alternative formulation that avoids the apparent circular references:
    # (Uncomment these and comment out the above if circular references cause issues)
    # dx1 = x7;  # Using indexed state variables: x1=q1, x7=dq1
    # dx2 = x8;  # Using indexed state variables: x2=q2, x8=dq2
    # dx3 = x9;  # Using indexed state variables: x3=q3, x9=dq3
    # dx4 = x10; # Using indexed state variables: x4=q4, x10=dq4
    # dx5 = x11; # Using indexed state variables: x5=q5, x11=dq5
    # dx6 = x12; # Using indexed state variables: x6=q6, x12=dq6
    
    # Velocity derivatives from torque inputs and system dynamics
    # In reality, these would involve the full manipulator dynamics equations
    # including the mass matrix, Coriolis terms, gravity, etc.
    ddq1 = (tau1 - d1*dq1 - g*m1*l1*sin(q1))/I1;
    ddq2 = (tau2 - d2*dq2 - g*m2*l2*sin(q2))/I2;
    ddq3 = (tau3 - d3*dq3 - g*m3*l3*sin(q3))/I3;
    ddq4 = (tau4 - d4*dq4)/I4;
    ddq5 = (tau5 - d5*dq5)/I5;
    ddq6 = (tau6 - d6*dq6)/I6;
  };
}

# Define Lyapunov function for position tracking stability
lyapunov PositionTrackingLyap {
  system: Manipulator;
  
  # Energy-based Lyapunov function
  # Includes kinetic energy and position error
  V: 0.5*(I1*dq1**2 + I2*dq2**2 + I3*dq3**2 + I4*dq4**2 + I5*dq5**2 + I6*dq6**2) +
     0.5*k_p*((q1-q1_d)**2 + (q2-q2_d)**2 + (q3-q3_d)**2 + 
             (q4-q4_d)**2 + (q5-q5_d)**2 + (q6-q6_d)**2);
  
  # Parameters for Lyapunov function
  params: {
    k_p: 2.0;    # Position gain
    
    # Desired joint positions
    q1_d: 0.0;
    q2_d: 0.0;
    q3_d: 0.0;
    q4_d: 0.0;
    q5_d: 0.0;
    q6_d: 0.0;
  };
}

# Define barrier function for joint limits
barrier JointLimitsBarrier {
  system: Manipulator;
  
  # Safety constraint: joint limits
  # This barrier is positive when joints are within limits
  # B > 0 means safe, B = 0 at boundary, B < 0 means unsafe (joint limit violation)
  # We use a product of barrier functions for upper and lower limits
  B: (q1_max - q1)*(q1 - q1_min) * 
     (q2_max - q2)*(q2 - q2_min) * 
     (q3_max - q3)*(q3 - q3_min) * 
     (q4_max - q4)*(q4 - q4_min) * 
     (q5_max - q5)*(q5 - q5_min) * 
     (q6_max - q6)*(q6 - q6_min);
  
  params: {
    # Joint limits (radians)
    q1_min: -2.0;  q1_max: 2.0;
    q2_min: -1.5;  q2_max: 1.5;
    q3_min: -2.5;  q3_max: 2.5;
    q4_min: -1.8;  q4_max: 1.8;
    q5_min: -1.5;  q5_max: 1.5;
    q6_min: -3.0;  q6_max: 3.0;
    
    alpha: 1.0;  # Class-K function parameter
  };
  
  # Class-K function for barrier certificate
  alpha_fun: alpha * B;
}

# Define barrier function for self-collision avoidance
barrier SelfCollisionBarrier {
  system: Manipulator;
  
  # Safety constraint: minimum distance between links
  # B > 0 means safe (links are far enough apart)
  # B = 0 at boundary, B < 0 means unsafe (links too close or colliding)
  # In practice, this would involve forward kinematics and proximity calculations
  # Here, we use a simplified example
  B: d_min**2 - (l2*sin(q2) - l4*sin(q4))**2 - (l2*cos(q2) - l4*cos(q4))**2;
  
  params: {
    d_min: 0.1;   # Minimum safe distance between links
    alpha: 1.0;   # Class-K function parameter
  };
  
  # Class-K function for barrier certificate
  alpha_fun: alpha * B;
}

# Define barrier function for workspace constraints
barrier WorkspaceBarrier {
  system: Manipulator;
  
  # Safety constraint: end-effector stays within workspace
  # B > 0 means safe (end-effector within workspace)
  # B = 0 at boundary, B < 0 means unsafe (end-effector outside workspace)
  # This is a simplified version; real implementation would use forward kinematics
  # to compute end-effector position
  B: r_max**2 - (l1*cos(q1) + l2*cos(q1+q2) + l3*cos(q1+q2+q3))**2 - 
     (l1*sin(q1) + l2*sin(q1+q2) + l3*sin(q1+q2+q3))**2;
  
  params: {
    r_max: 1.0;   # Maximum workspace radius
    alpha: 1.0;   # Class-K function parameter
  };
  
  # Class-K function for barrier certificate
  alpha_fun: alpha * B;
}

# Define barrier function for human safety
barrier HumanSafetyBarrier {
  system: Manipulator;
  
  # Safety constraint: minimum distance to human
  # B > 0 means safe (arm is far enough from human)
  # B = 0 at boundary, B < 0 means unsafe (arm too close to human)
  # Simplified example; real implementation would use proximity sensors
  B: (x_h - (l1*cos(q1) + l2*cos(q1+q2)))**2 + 
     (y_h - (l1*sin(q1) + l2*sin(q1+q2)))**2 - d_safe**2;
  
  params: {
    x_h: 0.5;     # Human position x
    y_h: 0.5;     # Human position y
    d_safe: 0.3;  # Safe distance to human
    alpha: 1.0;   # Class-K function parameter
  };
  
  # Class-K function for barrier certificate
  alpha_fun: alpha * B;
}

# Define control mode for normal joint control
mode JointPositionControl {
  system: Manipulator;
  lyapunov: PositionTrackingLyap;
  barriers: [JointLimitsBarrier, WorkspaceBarrier];
  
  # Controller definition: PD control with gravity compensation
  controller: {
    # Computed torque control
    tau1 = k_p1*(q1_d - q1) + k_d1*(0 - dq1) + g*m1*l1*sin(q1);
    tau2 = k_p2*(q2_d - q2) + k_d2*(0 - dq2) + g*m2*l2*sin(q2);
    tau3 = k_p3*(q3_d - q3) + k_d3*(0 - dq3) + g*m3*l3*sin(q3);
    tau4 = k_p4*(q4_d - q4) + k_d4*(0 - dq4);
    tau5 = k_p5*(q5_d - q5) + k_d5*(0 - dq5);
    tau6 = k_p6*(q6_d - q6) + k_d6*(0 - dq6);
  };
  
  # Controller parameters
  params: {
    # Position gains
    k_p1: 10.0;
    k_p2: 10.0;
    k_p3: 10.0;
    k_p4: 8.0;
    k_p5: 5.0;
    k_p6: 3.0;
    
    # Derivative gains
    k_d1: 2.0;
    k_d2: 2.0;
    k_d3: 2.0;
    k_d4: 1.5;
    k_d5: 1.0;
    k_d6: 0.5;
  };
}

# Define control mode for human collaboration
mode HumanCollaborationControl {
  system: Manipulator;
  lyapunov: PositionTrackingLyap;
  barriers: [JointLimitsBarrier, SelfCollisionBarrier, HumanSafetyBarrier];
  
  # Controller definition: Impedance control for safe human interaction
  controller: {
    # Reduce stiffness when human is nearby
    k_p1_h = k_p1 * human_proximity_factor;
    k_p2_h = k_p2 * human_proximity_factor;
    k_p3_h = k_p3 * human_proximity_factor;
    k_p4_h = k_p4 * human_proximity_factor;
    k_p5_h = k_p5 * human_proximity_factor;
    k_p6_h = k_p6 * human_proximity_factor;
    
    # Increase damping when human is nearby
    k_d1_h = k_d1 * (2.0 - human_proximity_factor);
    k_d2_h = k_d2 * (2.0 - human_proximity_factor);
    k_d3_h = k_d3 * (2.0 - human_proximity_factor);
    k_d4_h = k_d4 * (2.0 - human_proximity_factor);
    k_d5_h = k_d5 * (2.0 - human_proximity_factor);
    k_d6_h = k_d6 * (2.0 - human_proximity_factor);
    
    # Computed torque control with variable gains
    tau1 = k_p1_h*(q1_d - q1) + k_d1_h*(0 - dq1) + g*m1*l1*sin(q1);
    tau2 = k_p2_h*(q2_d - q2) + k_d2_h*(0 - dq2) + g*m2*l2*sin(q2);
    tau3 = k_p3_h*(q3_d - q3) + k_d3_h*(0 - dq3) + g*m3*l3*sin(q3);
    tau4 = k_p4_h*(q4_d - q4) + k_d4_h*(0 - dq4);
    tau5 = k_p5_h*(q5_d - q5) + k_d5_h*(0 - dq5);
    tau6 = k_p6_h*(q6_d - q6) + k_d6_h*(0 - dq6);
  };
  
  # Controller parameters
  params: {
    # Base position gains (same as JointPositionControl)
    k_p1: 10.0;
    k_p2: 10.0;
    k_p3: 10.0;
    k_p4: 8.0;
    k_p5: 5.0;
    k_p6: 3.0;
    
    # Base derivative gains (same as JointPositionControl)
    k_d1: 2.0;
    k_d2: 2.0;
    k_d3: 2.0;
    k_d4: 1.5;
    k_d5: 1.0;
    k_d6: 0.5;
    
    # Human proximity factor (1.0 = far away, 0.1 = very close)
    # In practice, this would be computed from sensor data
    human_proximity_factor: 0.5;
  };
}

# Define a force control mode for contact tasks
mode ForceControl {
  system: Manipulator;
  barriers: [JointLimitsBarrier, SelfCollisionBarrier];
  
  # Controller definition: Hybrid position/force control
  controller: {
    # Assuming z-direction is force controlled
    # In practice, this would involve a selection matrix for position vs. force DOFs
    
    # Position control components (x,y directions)
    tau_pos1 = k_p1*(q1_d - q1) + k_d1*(0 - dq1);
    tau_pos2 = k_p2*(q2_d - q2) + k_d2*(0 - dq2);
    
    # Force control component (z direction)
    tau_force3 = k_f*(F_d - F_measured);
    
    # Orientation control
    tau_ori4 = k_p4*(q4_d - q4) + k_d4*(0 - dq4);
    tau_ori5 = k_p5*(q5_d - q5) + k_d5*(0 - dq5);
    tau_ori6 = k_p6*(q6_d - q6) + k_d6*(0 - dq6);
    
    # Gravity compensation
    tau_g1 = g*m1*l1*sin(q1);
    tau_g2 = g*m2*l2*sin(q2);
    tau_g3 = g*m3*l3*sin(q3);
    
    # Combined control
    tau1 = tau_pos1 + tau_g1;
    tau2 = tau_pos2 + tau_g2;
    tau3 = tau_force3 + tau_g3;
    tau4 = tau_ori4;
    tau5 = tau_ori5;
    tau6 = tau_ori6;
  };
  
  # Controller parameters
  params: {
    # Position gains
    k_p1: 10.0;
    k_p2: 10.0;
    k_p4: 8.0;
    k_p5: 5.0;
    k_p6: 3.0;
    
    # Derivative gains
    k_d1: 2.0;
    k_d2: 2.0;
    k_d4: 1.5;
    k_d5: 1.0;
    k_d6: 0.5;
    
    # Force control gain
    k_f: 0.1;
    
    # Desired force (N)
    F_d: 5.0;
    
    # Measured force (would come from sensor in practice)
    F_measured: 0.0;
  };
}
