  color: #f3f4f6;
  border-bottom-left-radius: 4px;
}

.message-timestamp {
  font-size: 10px;
  color: rgba(255, 255, 255, 0.6);
  margin-top: 4px;
  position: absolute;
  bottom: -16px;
}

.chat-message.user .message-timestamp {
  right: 4px;
}

.chat-message.assistant .message-timestamp {
  left: 4px;
}

/* Intent indicator */
.message-intent-indicator {
  position: absolute;
  top: -8px;
  right: -8px;
  width: 20px;
  height: 20px;
  border-radius: 50%;
  background-color: #7c3aed;
  color: white;
  font-size: 12px;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  border: none;
  font-weight: bold;
  transition: all 0.2s ease;
  z-index: 1;
  opacity: 0.7;
}

