  opacity: 1;
  transform: scale(1.1);
}

/* Reference indicator */
.message-reference-indicator {
  position: absolute;
  bottom: -24px;
  right: 4px;
  font-size: 12px;
  color: #9ca3af;
  background: none;
  border: none;
  cursor: pointer;
  padding: 2px 6px;
  display: flex;
  align-items: center;
  gap: 4px;
  transition: all 0.2s ease;
}

.message-reference-indicator:hover {
  color: #d1d5db;
}

/* References display */
.message-references {
  margin-top: 8px;
  background-color: #242424;
  border-radius: 8px;
  padding: 12px;
  width: 100%;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.15);
