}

.message-references h4 {
  margin: 0 0 8px 0;
  font-size: 14px;
  font-weight: 500;
  color: #9ca3af;
}

.reference-item {
  margin-bottom: 8px;
  padding: 8px;
  background-color: #1c1c1c;
  border-radius: 6px;
  position: relative;
}

.reference-score {
  position: absolute;
  top: 8px;
  right: 8px;
  font-size: 10px;
  color: #6b7280;
  background-color: #111;
  padding: 2px 6px;
  border-radius: 12px;
}

.reference-content {
  margin-top: 4px;
  font-size: 13px;
  color: #d1d5db;
  line-height: 1.4;
}

.reference-timestamp {
  margin-top: 4px;
  font-size: 10px;
  color: #6b7280;
  text-align: right;
}

/* Input form */
.chat-history-input-form {
  display: flex;
  padding: 12px 16px;
  gap: 8px;
  background-color: #242424;
  border-top: 1px solid #333;
