}

.chat-history-input:focus {
  border-color: #2563eb;
}

.chat-history-input:disabled {
  opacity: 0.6;
  cursor: not-allowed;
}

.chat-history-send-button {
  padding: 0 16px;
  border: none;
  border-radius: 8px;
  background-color: #2563eb;
  color: white;
  font-weight: 500;
  font-size: 14px;
