  margin-bottom: 12px;
}

@keyframes spin {
  to { transform: rotate(360deg); }
}

.chat-history-loading p {
  color: #9ca3af;
  font-size: 14px;
}

/* Error state */
.chat-history-error {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100%;
  padding: 24px;
  text-align: center;
}

.chat-history-error p {
  color: #ef4444;
  font-size: 14px;
  margin-bottom: 16px;
}

.chat-history-error-button {
  padding: 8px 16px;
  border: none;
  border-radius: 6px;
  background-color: #1f2937;
  color: #f3f4f6;
  font-size: 14px;
  cursor: pointer;
  transition: background-color 0.2s ease;
}

.chat-history-error-button:hover {
  background-color: #374151;
}

/* Error alert */
.chat-history-alert {
  margin: 12px;
  padding: 12px;
  border-radius: 6px;
  position: relative;
  animation: slideDown 0.3s ease;
}

.chat-history-alert.error {
  background-color: rgba(239, 68, 68, 0.1);
  border-left: 4px solid #ef4444;
}

.chat-history-alert p {
  margin: 0;
  padding-right: 24px;
  font-size: 14px;
  color: #f3f4f6;
}

.chat-history-alert-dismiss {
  position: absolute;
  top: 8px;
  right: 8px;
  background: none;
  border: none;
  color: #9ca3af;
  font-size: 16px;
  cursor: pointer;
  padding: 4px;
  display: flex;
  align-items: center;
  justify-content: center;
}

/* Intent summary modal */
.intent-summary-overlay {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: rgba(0, 0, 0, 0.7);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
  animation: fadeIn 0.2s ease;
}

.intent-summary-modal {
  width: 90%;
  max-width: 500px;
  background-color: #1a1a1a;
  border-radius: 12px;
  padding: 24px;
  position: relative;
  box-shadow: 0 8px 24px rgba(0, 0, 0, 0.3);
  animation: slideUp 0.3s ease;
}

.intent-summary-close {
  position: absolute;
  top: 16px;
  right: 16px;
  background: none;
  border: none;
  color: #9ca3af;
  font-size: 24px;
  cursor: pointer;
  line-height: 1;
  padding: 0;
}

.intent-summary-modal h3 {
  margin: 0 0 16px 0;
  color: #f3f4f6;
  font-size: 18px;
  font-weight: 600;
}

.intent-summary-content {
  display: flex;
  flex-direction: column;
  gap: 12px;
}

.intent-type, .intent-description, .intent-confidence, .intent-entities {
  font-size: 14px;
  line-height: 1.5;
  color: #d1d5db;
}

.intent-type strong, .intent-description strong, .intent-confidence strong, .intent-entities strong {
  color: #f3f4f6;
  margin-right: 6px;
}

.intent-entities ul {
  margin: 8px 0 0 0;
  padding-left: 24px;
  color: #9ca3af;
}

.intent-entities li {
  margin-bottom: 4px;
}

/* Animations */
@keyframes fadeIn {
  from { opacity: 0; }
  to { opacity: 1; }
}

@keyframes slideDown {
  from { transform: translateY(-20px); opacity: 0; }
  to { transform: translateY(0); opacity: 1; }
}

@keyframes slideUp {
  from { transform: translateY(20px); opacity: 0; }
  to { transform: translateY(0); opacity: 1; }
}

/* Accessibility - respect reduced motion preferences */
@media (prefers-reduced-motion: reduce) {
  .chat-history-container,
  .chat-history-input,
  .chat-history-send-button,
  .message-intent-indicator,
  .message-reference-indicator,
  .intent-summary-overlay,
  .intent-summary-modal,
  .message-references {
    animation: none !important;
    transition: none !important;
  }
  
  .chat-history-loading-spinner {
    animation: none !important;
    border: 3px solid #3b82f6;
  }
  
  .chat-history-messages {
    scroll-behavior: auto;
  }
}

/* Responsive adjustments */
@media (max-width: 640px) {
  .chat-message {
    max-width: 90%;
  }
  
  .message-content {
    padding: 10px 12px;
    font-size: 13px;
  }
  
  .chat-history-input-form {
    padding: 8px 12px;
  }
  
  .intent-summary-modal {
    width: 95%;
    padding: 16px;
  }
}


