  const now = Date.now();
  const hour = new Date().getHours();
  
  // Late night coding detection (10 PM - 5 AM)
  if ((hour >= 22 || hour <= 5) && 
      systemState.activityLevel > 0.3 && 
      now - nuggetActivationHistory[NuggetTriggerType.LATE_NIGHT] > COOLDOWN_PERIODS[NuggetTriggerType.LATE_NIGHT]) {
