    position?: number;
    phaseTexture?: WebGLUniformLocation | null;
    nEffective?: WebGLUniformLocation | null;
    time?: WebGLUniformLocation | null;
    resolution?: WebGLUniformLocation | null;
    nodeCount?: WebGLUniformLocation | null;
  };

