.ws-status-dot {
  width: 10px;
  height: 10px;
  border-radius: 50%;
  display: inline-block;
}

.ws-status-connected {
  background-color: #4caf50; /* Green */
  box-shadow: 0 0 4px 1px rgba(76, 175, 80, 0.5);
}

.ws-status-connecting {
  background-color: #ffc107; /* Yellow */
  box-shadow: 0 0 4px 1px rgba(255, 193, 7, 0.5);
  animation: pulse 1.5s infinite;
}

.ws-status-disconnected {
  background-color: #f44336; /* Red */
  box-shadow: 0 0 4px 1px rgba(244, 67, 54, 0.5);
}

.ws-status-text {
  color: var(--color-text-secondary, #9e9e9e);
  font-weight: 500;
}

.ws-reconnect-button {
  background-color: transparent;
  color: var(--color-primary, #3b82f6);
  border: 1px solid var(--color-primary, #3b82f6);
  border-radius: 4px;
  padding: 2px 8px;
  font-size: 0.75rem;
  cursor: pointer;
  transition: all 0.2s ease-in-out;
}

.ws-reconnect-button:hover {
  background-color: var(--color-primary, #3b82f6);
  color: white;
}

@keyframes pulse {
  0% {
    opacity: 0.6;
    transform: scale(0.9);
  }
  50% {
    opacity: 1;
    transform: scale(1.1);
  }
  100% {
    opacity: 0.6;
    transform: scale(0.9);
  }
}

/* Dark mode adjustments if needed */
@media (prefers-color-scheme: dark) {
  .ws-status-text {
    color: var(--color-text-secondary-dark, #b0b0b0);
  }
}

