const { spawn } = require('child_process');
const http = require('http');

// Function to make an HTTP request to the MCP server
function makeRequest(path, method = 'GET', data = null) {
  return new Promise((resolve, reject) => {
    const options = {
      hostname: 'localhost',
      port: 3030,
      path,
      method,
      headers: {
        'Content-Type': 'application/json',
      },
    };

    const req = http.request(options, (res) => {
      let responseData = '';
      
      res.on('data', (chunk) => {
        responseData += chunk;
      });
      
      res.on('end', () => {
        try {
          const parsedData = JSON.parse(responseData);
          resolve(parsedData);
        } catch (error) {
          resolve(responseData);
        }
      });
    });

    req.on('error', (error) => {
      reject(error);
    });

    if (data) {
      req.write(JSON.stringify(data));
    }
    
    req.end();
  });
}

async function testFilesystemServer() {
  console.log('Testing connection to MCP Filesystem server...');
  try {
    // List files from C:\users directory (root of the filesystem server)
    const listFilesResponse = await makeRequest('/filesystem/api/v1/resources/list');
    console.log('Files in C:\\users:');
    console.log(JSON.stringify(listFilesResponse, null, 2));
    
    // If successful, try to access a file or directory info
    if (listFilesResponse && listFilesResponse.resources && listFilesResponse.resources.length > 0) {
      const sampleResource = listFilesResponse.resources[0];
      console.log(`\nGetting details for: ${sampleResource.name}`);
      const resourceDetails = await makeRequest(`/filesystem/api/v1/resources/info?path=${encodeURIComponent(sampleResource.name)}`);
      console.log(JSON.stringify(resourceDetails, null, 2));
    }
    
    console.log('\nFilesystem MCP server test completed successfully!');
  } catch (error) {
    console.error('Error testing filesystem MCP server:', error.message);
  }
}

// Start the MCP server
console.log('Starting MCP servers...');
const mcpProcess = spawn('npx', ['-y', '@modelcontextprotocol/cli', 'serve', '.mcp.json'], {
  stdio: ['inherit', 'inherit', 'inherit'],
  shell: true
});

// Wait a moment for the server to start before testing
setTimeout(async () => {
  try {
    await testFilesystemServer();
  } catch (error) {
    console.error('Test failed:', error);
  } finally {
    console.log('Shutting down MCP servers...');
    mcpProcess.kill();
    process.exit(0);
  }
}, 5000);

// Handle shutdown
process.on('SIGINT', () => {
  mcpProcess.kill();
  process.exit(0);
});

